     1→/*
     2→ * builtin_coroutine.c - TCL Coroutine Command Implementations
     3→ *
     4→ * Coroutine commands: coroutine, yield, yieldto
     5→ *
     6→ * Coroutines allow suspending and resuming execution. When a coroutine
     7→ * yields, it saves its state and returns to the caller. When resumed,
     8→ * it continues from where it left off.
     9→ */
    10→
    11→#include "internal.h"
    12→#include <string.h>
    13→
    14→/* ========================================================================
    15→ * Coroutine State
    16→ * ======================================================================== */
    17→
    18→typedef struct TclCoroutine {
    19→    char       *name;           /* Coroutine name (fully qualified) */
    20→    size_t      nameLen;        /* Length of name */
    21→    TclFrame   *savedFrame;     /* Saved frame for coroutine */
    22→    TclFrame   *baseFrame;      /* Frame where coroutine was created */
    23→    int         running;        /* Currently executing */
    24→    int         done;           /* Finished (returned or errored) */
    25→    TclObj     *result;         /* Last result/yield value */
    26→    TclInterp  *interp;         /* Interpreter */
    27→
    28→    /* For initial invocation */
    29→    TclObj    **cmdObjs;        /* Command + args to execute */
    30→    int         cmdObjc;        /* Count of command objects */
    31→    int         started;        /* Has first execution started? */
    32→
    33→    /* For script execution and resumption using yield counting */
    34→    const char *script;         /* Script being executed (proc body) */
    35→    size_t      scriptLen;      /* Script length */
    36→    int         yieldCount;     /* Number of yields executed so far */
    37→    int         yieldTarget;    /* Target yield to stop at on resume */
    38→    TclObj     *resumeValue;    /* Value to return from yield on resume */
    39→} TclCoroutine;
    40→
    41→/* Maximum number of active coroutines */
    42→#define MAX_COROUTINES 64
    43→static TclCoroutine *gCoroutines[MAX_COROUTINES];
    44→static int gCoroutineCount = 0;
    45→
    46→/* Current coroutine being executed (for yield to find) */
    47→static TclCoroutine *gCurrentCoroutine = NULL;
    48→
    49→/* Global yield flag - set by yield, checked by eval loop */
    50→static int gYieldPending = 0;
    51→
    52→/* Check if yield is pending (called by eval loop) */
    53→int tclCoroYieldPending(void) {
    54→    return gYieldPending;
    55→}
    56→
    57→/* Clear yield pending flag */
    58→void tclCoroClearYield(void) {
    59→    gYieldPending = 0;
    60→}
    61→
    62→/* ========================================================================
    63→ * Coroutine Management
    64→ * ======================================================================== */
    65→
    66→static TclCoroutine *coroFind(const char *name, size_t len) {
    67→    for (int i = 0; i < gCoroutineCount; i++) {
    68→        if (gCoroutines[i] && gCoroutines[i]->nameLen == len &&
    69→            tclStrncmp(gCoroutines[i]->name, name, len) == 0) {
    70→            return gCoroutines[i];
    71→        }
    72→    }
    73→    return NULL;
    74→}
    75→
    76→static TclCoroutine *coroFindByFullName(const char *fullName, size_t len) {
    77→    /* Try exact match first */
    78→    TclCoroutine *c = coroFind(fullName, len);
    79→    if (c) return c;
    80→
    81→    /* Try with :: prefix */
    82→    if (len >= 2 && fullName[0] == ':' && fullName[1] == ':') {
    83→        return coroFind(fullName + 2, len - 2);
    84→    }
    85→
    86→    /* Try adding :: prefix */
    87→    for (int i = 0; i < gCoroutineCount; i++) {
    88→        if (gCoroutines[i] && gCoroutines[i]->nameLen == len + 2 &&
    89→            gCoroutines[i]->name[0] == ':' && gCoroutines[i]->name[1] == ':' &&
    90→            tclStrncmp(gCoroutines[i]->name + 2, fullName, len) == 0) {
    91→            return gCoroutines[i];
    92→        }
    93→    }
    94→    return NULL;
    95→}
    96→
    97→static void coroFree(TclCoroutine *coro) {
    98→    if (!coro) return;
    99→
   100→    /* Remove from global list */
   101→    for (int i = 0; i < gCoroutineCount; i++) {
   102→        if (gCoroutines[i] == coro) {
   103→            gCoroutines[i] = gCoroutines[gCoroutineCount - 1];
   104→            gCoroutines[gCoroutineCount - 1] = NULL;
   105→            gCoroutineCount--;
   106→            break;
   107→        }
   108→    }
   109→
   110→    /* Note: we don't free the name/cmdObjs here as they're arena-allocated
   111→     * or managed by the interpreter. The coroutine struct itself is also
   112→     * arena-allocated in a real implementation. For now we leak. */
   113→}
   114→
   115→static TclCoroutine *coroCreate(TclInterp *interp, const char *name, size_t nameLen) {
   116→    if (gCoroutineCount >= MAX_COROUTINES) {
   117→        return NULL;
   118→    }
   119→
   120→    const TclHost *host = interp->host;
   121→    void *arena = host->arenaPush(interp->hostCtx);
   122→
   123→    TclCoroutine *coro = host->arenaAlloc(arena, sizeof(TclCoroutine), sizeof(void*));
   124→    if (!coro) {
   125→        host->arenaPop(interp->hostCtx, arena);
   126→        return NULL;
   127→    }
   128→
   129→    /* Build fully qualified name */
   130→    char *fullName;
   131→    size_t fullLen;
   132→    if (nameLen >= 2 && name[0] == ':' && name[1] == ':') {
   133→        fullName = host->arenaStrdup(arena, name, nameLen);
   134→        fullLen = nameLen;
   135→    } else {
   136→        fullName = host->arenaAlloc(arena, nameLen + 3, 1);
   137→        fullName[0] = ':';
   138→        fullName[1] = ':';
   139→        for (size_t i = 0; i < nameLen; i++) {
   140→            fullName[i + 2] = name[i];
   141→        }
   142→        fullName[nameLen + 2] = '\0';
   143→        fullLen = nameLen + 2;
   144→    }
   145→
   146→    coro->name = fullName;
   147→    coro->nameLen = fullLen;
   148→    coro->savedFrame = NULL;
   149→    coro->baseFrame = interp->currentFrame;
   150→    coro->running = 0;
   151→    coro->done = 0;
   152→    coro->result = NULL;
   153→    coro->interp = interp;
   154→    coro->cmdObjs = NULL;
   155→    coro->cmdObjc = 0;
   156→    coro->started = 0;
   157→    coro->script = NULL;
   158→    coro->scriptLen = 0;
   159→    coro->yieldCount = 0;
   160→    coro->yieldTarget = 0;
   161→    coro->resumeValue = NULL;
   162→
   163→    gCoroutines[gCoroutineCount++] = coro;
   164→
   165→    /* Note: arena is intentionally not popped - coro needs to persist */
   166→
   167→    return coro;
   168→}
   169→
   170→/* ========================================================================
   171→ * Coroutine Execution
   172→ * ======================================================================== */
   173→
   174→/* Evaluate script within coroutine context, using yield counting for resume */
   175→static TclResult coroEvalScript(TclCoroutine *coro) {
   176→    TclInterp *interp = coro->interp;
   177→
   178→    /* Always execute from the beginning - yield counting handles resume */
   179→    TclResult result = tclEvalScript(interp, coro->script, coro->scriptLen);
   180→
   181→    return result;
   182→}
   183→
   184→/* Execute the coroutine's command (first invocation or resume) */
   185→static TclResult coroExecute(TclCoroutine *coro, TclObj *resumeValue) {
   186→    TclInterp *interp = coro->interp;
   187→    const TclHost *host = interp->host;
   188→
   189→    if (coro->done) {
   190→        tclSetError(interp, "invalid command name", -1);
   191→        return TCL_ERROR;
   192→    }
   193→
   194→    if (coro->running) {
   195→        tclSetError(interp, "coroutine is already running", -1);
   196→        return TCL_ERROR;
   197→    }
   198→
   199→    coro->running = 1;
   200→    coro->resumeValue = resumeValue;
   201→    TclCoroutine *prevCoro = gCurrentCoroutine;
   202→    gCurrentCoroutine = coro;
   203→    gYieldPending = 0;
   204→
   205→    TclResult result;
   206→    TclFrame *savedFrame = interp->currentFrame;
   207→
   208→    if (!coro->started) {
   209→        /* First invocation - set up the coroutine */
   210→        coro->started = 1;
   211→
   212→        if (coro->cmdObjc < 1) {
   213→            coro->running = 0;
   214→            gCurrentCoroutine = prevCoro;
   215→            tclSetError(interp, "no command to execute", -1);
   216→            return TCL_ERROR;
   217→        }
   218→
   219→        /* Create the coroutine frame */
   220→        TclFrame *coroFrame = host->frameAlloc(interp->hostCtx);
   221→        if (!coroFrame) {
   222→            coro->running = 0;
   223→            gCurrentCoroutine = prevCoro;
   224→            tclSetError(interp, "out of memory", -1);
   225→            return TCL_ERROR;
   226→        }
   227→        coroFrame->parent = interp->globalFrame;
   228→        coroFrame->level = 1;
   229→        coroFrame->flags = TCL_FRAME_PROC | TCL_FRAME_COROUTINE;
   230→        coroFrame->procName = coro->name;
   231→        coroFrame->invocationObjs = coro->cmdObjs;
   232→        coroFrame->invocationCount = coro->cmdObjc;
   233→
   234→        coro->savedFrame = coroFrame;
   235→
   236→        /* Look up the command */
   237→        size_t cmdLen;
   238→        const char *cmdName = host->getStringPtr(coro->cmdObjs[0], &cmdLen);
   239→
   240→        int builtinIdx = tclBuiltinLookup(cmdName, cmdLen);
   241→        TclCmdInfo cmdInfo;
   242→
   243→        if (builtinIdx >= 0) {
   244→            cmdInfo.type = TCL_CMD_BUILTIN;
   245→            cmdInfo.u.builtinId = builtinIdx;
   246→        } else if (host->cmdLookup(interp->hostCtx, cmdName, cmdLen, &cmdInfo) != 0) {
   247→            cmdInfo.type = TCL_CMD_NOT_FOUND;
   248→        }
   249→
   250→        if (cmdInfo.type == TCL_CMD_PROC) {
   251→            /* Get proc definition and save script */
   252→            TclObj *argList = NULL;
   253→            TclObj *body = NULL;
   254→            if (host->procGetDef(cmdInfo.u.procHandle, &argList, &body) != 0) {
   255→                coro->running = 0;
   256→                gCurrentCoroutine = prevCoro;
   257→                host->frameFree(interp->hostCtx, coroFrame);
   258→                tclSetError(interp, "proc definition not found", -1);
   259→                return TCL_ERROR;
   260→            }
   261→
   262→            /* Parse and bind arguments */
   263→            TclObj **argSpecs = NULL;
   264→            size_t argCount = 0;
   265→            host->asList(argList, &argSpecs, &argCount);
   266→
   267→            int actualArgs = coro->cmdObjc - 1;
   268→            int hasArgs = 0;
   269→
   270→            if (argCount > 0) {
   271→                size_t lastArgLen;
   272→                const char *lastArg = host->getStringPtr(argSpecs[argCount - 1], &lastArgLen);
   273→                if (lastArgLen == 4 && tclStrncmp(lastArg, "args", 4) == 0) {
   274→                    hasArgs = 1;
   275→                }
   276→            }
   277→
   278→            int requiredArgs = (int)argCount - (hasArgs ? 1 : 0);
   279→
   280→            /* Bind arguments to frame */
   281→            interp->currentFrame = coroFrame;
   282→            for (size_t i = 0; i < (size_t)requiredArgs; i++) {
   283→                TclObj *argSpec = argSpecs[i];
   284→                size_t argNameLen;
   285→                const char *argName;
   286→                TclObj *value = NULL;
   287→
   288→                size_t listLen = host->listLength(argSpec);
   289→                if (listLen >= 2) {
   290→                    TclObj *nameObj = host->listIndex(argSpec, 0);
   291→                    argName = host->getStringPtr(nameObj, &argNameLen);
   292→                    if ((int)i < actualArgs) {
   293→                        value = coro->cmdObjs[i + 1];
   294→                    } else {
   295→                        value = host->listIndex(argSpec, 1);
   296→                    }
   297→                } else {
   298→                    argName = host->getStringPtr(argSpec, &argNameLen);
   299→                    if ((int)i < actualArgs) {
   300→                        value = coro->cmdObjs[i + 1];
   301→                    }
   302→                }
   303→
   304→                if (value) {
   305→                    host->varSet(coroFrame->varsHandle, argName, argNameLen, host->dup(value));
   306→                }
   307→            }
   308→
   309→            if (hasArgs) {
   310→                int argsStart = requiredArgs;
   311→                int argsCount = actualArgs - argsStart;
   312→                if (argsCount < 0) argsCount = 0;
   313→
   314→                TclObj *argsList;
   315→                if (argsCount > 0) {
   316→                    argsList = host->newList(&coro->cmdObjs[argsStart + 1], argsCount);
   317→                } else {
   318→                    argsList = host->newString("", 0);
   319→                }
   320→                host->varSet(coroFrame->varsHandle, "args", 4, argsList);
   321→            }
   322→
   323→            /* Save script for resumption */
   324→            size_t bodyLen;
   325→            const char *bodyStr = host->getStringPtr(body, &bodyLen);
   326→            coro->script = bodyStr;
   327→            coro->scriptLen = bodyLen;
   328→
   329→            /* Execute with yield counting support */
   330→            result = coroEvalScript(coro);
   331→        } else if (cmdInfo.type == TCL_CMD_BUILTIN) {
   332→            interp->currentFrame = coroFrame;
   333→            const TclBuiltinEntry *entry = tclBuiltinGet(cmdInfo.u.builtinId);
   334→            if (entry) {
   335→                result = entry->proc(interp, coro->cmdObjc, coro->cmdObjs);
   336→            } else {
   337→                result = TCL_ERROR;
   338→                tclSetError(interp, "invalid builtin", -1);
   339→            }
   340→        } else {
   341→            coro->running = 0;
   342→            gCurrentCoroutine = prevCoro;
   343→            host->frameFree(interp->hostCtx, coroFrame);
   344→            tclSetError(interp, "invalid command name", -1);
   345→            return TCL_ERROR;
   346→        }
   347→    } else {
   348→        /* Resume - re-execute script with yield counting */
   349→        interp->currentFrame = coro->savedFrame;
   350→
   351→        /* Set yieldTarget to skip to where we were */
   352→        coro->yieldTarget = coro->yieldCount;
   353→        coro->yieldCount = 0;
   354→
   355→        if (coro->script) {
   356→            result = coroEvalScript(coro);
   357→        } else {
   358→            result = TCL_OK;
   359→        }
   360→    }
   361→
   362→    interp->currentFrame = savedFrame;
   363→
   364→    /* Check if we yielded or finished */
   365→    if (gYieldPending) {
   366→        /* Yielded - keep coroutine alive */
   367→        gYieldPending = 0;
   368→        coro->running = 0;
   369→        gCurrentCoroutine = prevCoro;
   370→        tclSetResult(interp, coro->result);
   371→        return TCL_OK;
   372→    }
   373→
   374→    /* Finished - clean up */
   375→    coro->done = 1;
   376→    coro->running = 0;
   377→    gCurrentCoroutine = prevCoro;
   378→
   379→    if (result == TCL_RETURN) {
   380→        result = TCL_OK;
   381→    }
   382→    if (result == TCL_OK) {
   383→        coro->result = interp->result;
   384→    }
   385→
   386→    /* Free the coroutine frame */
   387→    if (coro->savedFrame) {
   388→        host->frameFree(interp->hostCtx, coro->savedFrame);
   389→        coro->savedFrame = NULL;
   390→    }
   391→
   392→    tclSetResult(interp, coro->result ? coro->result : host->newString("", 0));
   393→    return result;
   394→}
   395→
   396→/* ========================================================================
   397→ * coroutine Command
   398→ *
   399→ * coroutine name command ?arg ...?
   400→ *
   401→ * Creates a new coroutine with the given name and starts executing
   402→ * the command. Returns the first yielded value or the final result.
   403→ * ======================================================================== */
   404→
   405→TclResult tclCmdCoroutine(TclInterp *interp, int objc, TclObj **objv) {
   406→    const TclHost *host = interp->host;
   407→
   408→    if (objc < 3) {
   409→        tclSetError(interp, "wrong # args: should be \"coroutine name command ?arg ...?\"", -1);
   410→        return TCL_ERROR;
   411→    }
   412→
   413→    size_t nameLen;
   414→    const char *name = host->getStringPtr(objv[1], &nameLen);
   415→
   416→    /* Check if a coroutine with this name already exists */
   417→    if (coroFindByFullName(name, nameLen)) {
   418→        tclSetError(interp, "command already exists", -1);
   419→        return TCL_ERROR;
   420→    }
   421→
   422→    /* Create the coroutine */
   423→    TclCoroutine *coro = coroCreate(interp, name, nameLen);
   424→    if (!coro) {
   425→        tclSetError(interp, "cannot create coroutine", -1);
   426→        return TCL_ERROR;
   427→    }
   428→
   429→    /* Store the command to execute */
   430→    coro->cmdObjs = &objv[2];
   431→    coro->cmdObjc = objc - 2;
   432→
   433→    /* Execute the coroutine (starts the command) */
   434→    TclResult result = coroExecute(coro, NULL);
   435→
   436→    if (result != TCL_OK && !coro->done) {
   437→        /* Error during initial execution */
   438→        coroFree(coro);
   439→        return result;
   440→    }
   441→
   442→    /* Return the first yield value or final result */
   443→    if (coro->result) {
   444→        tclSetResult(interp, coro->result);
   445→    } else {
   446→        tclSetResult(interp, host->newString("", 0));
   447→    }
   448→
   449→    /* If coroutine finished immediately, clean up */
   450→    if (coro->done) {
   451→        coroFree(coro);
   452→    }
   453→
   454→    return TCL_OK;
   455→}
   456→
   457→/* ========================================================================
   458→ * yield Command
   459→ *
   460→ * yield ?value?
   461→ *
   462→ * Suspends the current coroutine and returns value to the caller.
   463→ * When resumed, returns the value passed by the caller.
   464→ * ======================================================================== */
   465→
   466→TclResult tclCmdYield(TclInterp *interp, int objc, TclObj **objv) {
   467→    const TclHost *host = interp->host;
   468→
   469→    if (objc > 2) {
   470→        tclSetError(interp, "wrong # args: should be \"yield ?value?\"", -1);
   471→        return TCL_ERROR;
   472→    }
   473→
   474→    /* Check if we're in a coroutine */
   475→    if (!gCurrentCoroutine) {
   476→        tclSetError(interp, "yield can only be called inside a coroutine", -1);
   477→        return TCL_ERROR;
   478→    }
   479→
   480→    TclCoroutine *coro = gCurrentCoroutine;
   481→
   482→    /* Check if we should skip this yield (replaying to catch up) */
   483→    if (coro->yieldCount < coro->yieldTarget) {
   484→        /* Skip this yield - return the resume value instead */
   485→        coro->yieldCount++;
   486→        tclSetResult(interp, coro->resumeValue ? coro->resumeValue : host->newString("", 0));
   487→        return TCL_OK;
   488→    }
   489→
   490→    /* Get the yield value */
   491→    TclObj *value;
   492→    if (objc == 2) {
   493→        value = objv[1];
   494→    } else {
   495→        value = host->newString("", 0);
   496→    }
   497→
   498→    /* Actually yield - increment count and signal */
   499→    coro->yieldCount++;
   500→    coro->result = value;
   501→    coro->running = 0;
   502→    gYieldPending = 1;
   503→
   504→    /* Return the value - this will be the result of the coroutine call */
   505→    tclSetResult(interp, value);
   506→
   507→    return TCL_OK;
   508→}
   509→
   510→/* ========================================================================
   511→ * yieldto Command
   512→ *
   513→ * yieldto command ?arg ...?
   514→ *
   515→ * Suspends the current coroutine and calls command. The return value
   516→ * of command becomes the result returned to the coroutine caller.
   517→ * When the coroutine is resumed, the arguments passed become yield's
   518→ * return value.
   519→ * ======================================================================== */
   520→
   521→TclResult tclCmdYieldto(TclInterp *interp, int objc, TclObj **objv) {
   522→    const TclHost *host = interp->host;
   523→
   524→    if (objc < 2) {
   525→        tclSetError(interp, "wrong # args: should be \"yieldto command ?arg ...?\"", -1);
   526→        return TCL_ERROR;
   527→    }
   528→
   529→    /* Check if we're in a coroutine */
   530→    if (!gCurrentCoroutine) {
   531→        tclSetError(interp, "yieldto can only be called inside a coroutine", -1);
   532→        return TCL_ERROR;
   533→    }
   534→
   535→    /* Execute the command */
   536→    size_t cmdLen;
   537→    const char *cmdName = host->getStringPtr(objv[1], &cmdLen);
   538→
   539→    /* Look up command */
   540→    int builtinIdx = tclBuiltinLookup(cmdName, cmdLen);
   541→    TclCmdInfo cmdInfo;
   542→
   543→    if (builtinIdx >= 0) {
   544→        cmdInfo.type = TCL_CMD_BUILTIN;
   545→        cmdInfo.u.builtinId = builtinIdx;
   546→    } else if (host->cmdLookup(interp->hostCtx, cmdName, cmdLen, &cmdInfo) != 0) {
   547→        cmdInfo.type = TCL_CMD_NOT_FOUND;
   548→    }
   549→
   550→    TclResult result;
   551→
   552→    switch (cmdInfo.type) {
   553→        case TCL_CMD_BUILTIN: {
   554→            const TclBuiltinEntry *entry = tclBuiltinGet(cmdInfo.u.builtinId);
   555→            if (entry) {
   556→                result = entry->proc(interp, objc - 1, &objv[1]);
   557→            } else {
   558→                tclSetError(interp, "invalid builtin", -1);
   559→                result = TCL_ERROR;
   560→            }
   561→            break;
   562→        }
   563→        case TCL_CMD_PROC: {
   564→            TclObj *argList = NULL;
   565→            TclObj *body = NULL;
   566→            if (host->procGetDef(cmdInfo.u.procHandle, &argList, &body) != 0) {
   567→                tclSetError(interp, "proc definition not found", -1);
   568→                result = TCL_ERROR;
   569→                break;
   570→            }
   571→            size_t bodyLen;
   572→            const char *bodyStr = host->getStringPtr(body, &bodyLen);
   573→            result = tclEvalScript(interp, bodyStr, bodyLen);
   574→            break;
   575→        }
   576→        case TCL_CMD_EXTENSION:
   577→            result = host->extInvoke(interp, cmdInfo.u.extHandle, objc - 1, &objv[1]);
   578→            break;
   579→        default:
   580→            tclSetError(interp, "invalid command name", -1);
   581→            result = TCL_ERROR;
   582→            break;
   583→    }
   584→
