   603→    gYieldPending = 1;
   604→
   605→    return TCL_OK;
   606→}
   607→
   608→/* ========================================================================
   609→ * Coroutine Invocation (when calling the coroutine by name)
   610→ *
   611→ * This is called when the coroutine command is invoked to resume it.
   612→ * ======================================================================== */
   613→
   614→TclResult tclCoroInvoke(TclInterp *interp, TclCoroutine *coro, int objc, TclObj **objv) {
   615→    const TclHost *host = interp->host;
