typedef enum {
    LOOP_TYPE_WHILE,
    LOOP_TYPE_FOR,
    LOOP_TYPE_FOREACH
} TclLoopType;

typedef struct TclLoopState {
    TclLoopType     type;
    TclLoopPhase    phase;

    /* Common fields */
    const char     *bodyStr;
    size_t          bodyLen;
    size_t          bodyResumeOffset;  /* Offset into body to resume from after yield */

    /* For while/for: condition */
