    size_t          testLen;

    /* For for: next clause */
    const char     *nextStr;
    size_t          nextLen;

    /* For foreach: iteration state */
    TclObj        **elems;        /* List elements */
    size_t          elemCount;
    size_t          currentIndex; /* Which element we're on */
    const char     *varName;      /* Variable name */
    size_t          varNameLen;

