    13→
    14→int tclStrncmp(const char *a, const char *b, size_t n) {
    15→    for (size_t i = 0; i < n; i++) {
    16→        if (a[i] != b[i]) {
    17→            return (unsigned char)a[i] - (unsigned char)b[i];
    18→        }
    19→        if (a[i] == '\0') {
    20→            return 0;
    21→        }
    22→    }
    23→    return 0;
    24→}
    25→
    26→size_t tclStrlen(const char *s) {
    27→    size_t len = 0;
    28→    while (s[len]) len++;
    29→    return len;
    30→}
