    32→/* ========================================================================
    33→ * Lexer Initialization
    34→ * ======================================================================== */
    35→
    36→void tclLexerInit(TclLexer *lex, const char *script, size_t len) {
    37→    lex->script = script;
    38→    lex->pos = script;
    39→    lex->end = script + len;
    40→    lex->line = 1;
    41→}
    42→
    43→/* ========================================================================
    44→ * Whitespace and Position Helpers
    45→ * ======================================================================== */
    46→
    47→void tclLexerSkipSpace(TclLexer *lex) {
    48→    while (lex->pos < lex->end) {
    49→        if (*lex->pos == ' ' || *lex->pos == '\t') {
    50→            lex->pos++;
    51→        } else if (*lex->pos == '\\' && lex->pos + 1 < lex->end && lex->pos[1] == '\n') {
    52→            /* Backslash-newline acts as whitespace */
    53→            lex->pos += 2;
    54→            lex->line++;
    55→        } else {
    56→            break;
    57→        }
    58→    }
    59→}
    60→
    61→void tclLexerSkipLine(TclLexer *lex) {
    62→    while (lex->pos < lex->end && *lex->pos != '\n') {
    63→        lex->pos++;
    64→    }
    65→    if (lex->pos < lex->end && *lex->pos == '\n') {
    66→        lex->pos++;
    67→        lex->line++;
    68→    }
    69→}
