    99→
   100→/* Parse a brace-quoted word: {content} */
   101→static int lexBraces(TclLexer *lex, TclWord *word, TclInterp *interp) {
   102→    int depth = 1;
   103→    const char *start = lex->pos;  /* After opening { */
   104→    int startLine = lex->line;
   105→
   106→    while (lex->pos < lex->end && depth > 0) {
   107→        if (*lex->pos == '\\' && lex->pos + 1 < lex->end) {
   108→            /* Skip escaped char, including \{ and \} */
   109→            if (lex->pos[1] == '\n') lex->line++;
   110→            lex->pos += 2;
   111→        } else if (*lex->pos == '{') {
   112→            depth++;
   113→            lex->pos++;
   114→        } else if (*lex->pos == '}') {
   115→            depth--;
   116→            if (depth > 0) lex->pos++;
   117→        } else {
   118→            if (*lex->pos == '\n') lex->line++;
   119→            lex->pos++;
   120→        }
   121→    }
   122→
   123→    if (depth != 0) {
   124→        if (interp) {
   125→            tclSetError(interp, "missing close-brace", -1);
   126→        }
   127→        return -1;
   128→    }
   129→
   130→    word->start = start;
   131→    word->len = lex->pos - start;
   132→    word->type = TCL_WORD_BRACES;
   133→    word->line = startLine;
   134→
   135→    lex->pos++;  /* Skip closing } */
   136→    return 0;
   137→}
   138→
   139→/* Parse a double-quoted word: "content" */
   140→static int lexQuotes(TclLexer *lex, TclWord *word, TclInterp *interp) {
   141→    const char *start = lex->pos;  /* After opening " */
   142→    int startLine = lex->line;
   143→
   144→    while (lex->pos < lex->end && *lex->pos != '"') {
   145→        if (*lex->pos == '\\' && lex->pos + 1 < lex->end) {
   146→            if (lex->pos[1] == '\n') lex->line++;
   147→            lex->pos += 2;
   148→        } else if (*lex->pos == '[') {
   149→            /* Command substitution - skip to matching ] */
   150→            int depth = 1;
   151→            lex->pos++;
   152→            while (lex->pos < lex->end && depth > 0) {
   153→                char ch = *lex->pos;
   154→                if (ch == '\\' && lex->pos + 1 < lex->end) {
   155→                    lex->pos += 2;
   156→                } else if (ch == '[') {
   157→                    depth++;
   158→                    lex->pos++;
   159→                } else if (ch == ']') {
   160→                    depth--;
   161→                    if (depth > 0) lex->pos++;
   162→                } else if (ch == '{') {
   163→                    /* Skip braced content */
   164→                    int braceDepth = 1;
   165→                    lex->pos++;
   166→                    while (lex->pos < lex->end && braceDepth > 0) {
   167→                        if (*lex->pos == '\\' && lex->pos + 1 < lex->end) {
   168→                            lex->pos += 2;
   169→                        } else {
   170→                            if (*lex->pos == '{') braceDepth++;
   171→                            else if (*lex->pos == '}') braceDepth--;
   172→                            if (braceDepth > 0) lex->pos++;
   173→                        }
   174→                    }
   175→                    if (lex->pos < lex->end) lex->pos++;  /* Skip } */
   176→                } else if (ch == '"') {
   177→                    /* Skip quoted content within command */
   178→                    lex->pos++;
   179→                    while (lex->pos < lex->end && *lex->pos != '"') {
   180→                        if (*lex->pos == '\\' && lex->pos + 1 < lex->end) {
   181→                            lex->pos += 2;
   182→                        } else {
   183→                            lex->pos++;
   184→                        }
   185→                    }
   186→                    if (lex->pos < lex->end) lex->pos++;  /* Skip " */
   187→                } else {
   188→                    if (ch == '\n') lex->line++;
   189→                    lex->pos++;
   190→                }
   191→            }
   192→            if (lex->pos < lex->end) lex->pos++;  /* Skip ] */
   193→        } else {
   194→            if (*lex->pos == '\n') lex->line++;
   195→            lex->pos++;
   196→        }
   197→    }
