   215→/* Parse a bare (unquoted) word */
   216→static int lexBareWord(TclLexer *lex, TclWord *word, TclInterp *interp) {
   217→    const char *start = lex->pos;
   218→    int startLine = lex->line;
   219→
   220→    /* Check for {*} expansion prefix at start */
   221→    if (lex->pos + 2 < lex->end &&
   222→        lex->pos[0] == '{' && lex->pos[1] == '*' && lex->pos[2] == '}') {
   223→        lex->pos += 3;  /* Skip {*} prefix, continue parsing the rest */
   224→    }
   225→
   226→    while (lex->pos < lex->end) {
   227→        char c = *lex->pos;
   228→
   229→        /* Word terminators */
   230→        if (c == ' ' || c == '\t' || c == '\n' || c == ';') {
   231→            break;
   232→        }
   233→
   234→        /* Backslash handling */
   235→        if (c == '\\' && lex->pos + 1 < lex->end) {
   236→            if (lex->pos[1] == '\n') {
   237→                /* Backslash-newline ends word (acts as whitespace) */
   238→                break;
   239→            } else {
   240→                /* Skip escaped character */
   241→                lex->pos += 2;
   242→                continue;
   243→            }
   244→        }
   245→
   246→        /* ${varname} - scan to matching } */
   247→        if (c == '$' && lex->pos + 1 < lex->end && lex->pos[1] == '{') {
   248→            lex->pos += 2;
   249→            while (lex->pos < lex->end && *lex->pos != '}') {
   250→                lex->pos++;
   251→            }
   252→            if (lex->pos < lex->end) lex->pos++;  /* Skip } */
   253→            continue;
   254→        }
   255→
   256→        /* $varname or $varname(key) - handle array element syntax */
   257→        if (c == '$') {
   258→            lex->pos++;  /* Skip $ */
   259→            /* Skip variable name */
   260→            while (lex->pos < lex->end) {
   261→                char vc = *lex->pos;
   262→                if ((vc >= 'a' && vc <= 'z') || (vc >= 'A' && vc <= 'Z') ||
   263→                    (vc >= '0' && vc <= '9') || vc == '_') {
   264→                    lex->pos++;
   265→                } else {
   266→                    break;
   267→                }
   268→            }
   269→            /* Check for (key) - array element */
   270→            if (lex->pos < lex->end && *lex->pos == '(') {
   271→                int parenDepth = 1;
   272→                lex->pos++;  /* Skip ( */
   273→                while (lex->pos < lex->end && parenDepth > 0) {
   274→                    if (*lex->pos == '(') parenDepth++;
   275→                    else if (*lex->pos == ')') parenDepth--;
   276→                    if (parenDepth > 0) lex->pos++;
   277→                }
   278→                if (lex->pos < lex->end) lex->pos++;  /* Skip ) */
   279→            }
   280→            continue;
   281→        }
   282→
   283→        /* These end a bare word unless escaped */
   284→        if (c == '"' || c == '{' || c == '}') {
   285→            break;
   286→        }
   287→
   288→        /* [command] substitution - scan to matching ] */
   289→        if (c == '[') {
   290→            int depth = 1;
   291→            lex->pos++;
   292→            while (lex->pos < lex->end && depth > 0) {
   293→                char ch = *lex->pos;
   294→                if (ch == '{') {
   295→                    /* Skip braced content - brackets inside don't count */
   296→                    int braceDepth = 1;
   297→                    lex->pos++;
   298→                    while (lex->pos < lex->end && braceDepth > 0) {
   299→                        if (*lex->pos == '{') braceDepth++;
   300→                        else if (*lex->pos == '}') braceDepth--;
   301→                        if (*lex->pos == '\n') lex->line++;
   302→                        if (braceDepth > 0) lex->pos++;
   303→                    }
   304→                    if (lex->pos < lex->end) lex->pos++;  /* Skip } */
   305→                    continue;  /* Re-check loop condition */
   306→                } else if (ch == '"') {
   307→                    /* Skip quoted content - brackets inside don't count */
   308→                    lex->pos++;
   309→                    while (lex->pos < lex->end && *lex->pos != '"') {
   310→                        if (*lex->pos == '\\' && lex->pos + 1 < lex->end) {
   311→                            lex->pos++;  /* Skip escaped char */
   312→                        }
   313→                        if (*lex->pos == '\n') lex->line++;
   314→                        lex->pos++;
   315→                    }
   316→                    if (lex->pos < lex->end) lex->pos++;  /* Skip " */
   317→                    continue;  /* Re-check loop condition */
   318→                } else {
   319→                    if (ch == '[') depth++;
   320→                    else if (ch == ']') depth--;
   321→                    if (ch == '\n') lex->line++;
   322→                    if (depth > 0) lex->pos++;
   323→                }
   324→            }
   325→            if (depth != 0) {
   326→                if (interp) {
   327→                    tclSetError(interp, "missing close-bracket", -1);
   328→                }
   329→                return -1;
   330→            }
   331→            if (lex->pos < lex->end) lex->pos++;  /* Skip ] */
   332→            continue;
   333→        }
   334→
   335→        lex->pos++;
   336→    }
   337→
   338→    word->start = start;
   339→    word->len = lex->pos - start;
   340→    word->type = TCL_WORD_BARE;
