     2→ * subst.c - TCL Substitution Engine
     3→ *
     4→ * Performs $var, [cmd], and \x substitution.
     5→ * Uses arena for intermediate strings, returns host-allocated TclObj.
     6→ */
     7→
     8→#include "internal.h"
     9→
    10→/* Forward declaration for command substitution */
    11→extern TclResult tclEvalBracketed(TclInterp *interp, const char *cmd, size_t len);
    12→
    13→/* ========================================================================
    14→ * Backslash Substitution
    15→ * ======================================================================== */
    16→
    17→/* Process a single backslash escape sequence.
    18→ * Returns number of input characters consumed (including backslash).
    19→ * Writes output character to *out.
    20→ */
    21→/* Helper to convert hex char to value */
    22→static int hexDigit(char c) {
    23→    if (c >= '0' && c <= '9') return c - '0';
    24→    if (c >= 'a' && c <= 'f') return c - 'a' + 10;
    25→    if (c >= 'A' && c <= 'F') return c - 'A' + 10;
    26→    return -1;
    27→}
    28→
    29→/* Helper to convert octal char to value */
    30→static int octDigit(char c) {
    31→    if (c >= '0' && c <= '7') return c - '0';
    32→    return -1;
    33→}
    34→
    35→int tclSubstBackslashChar(const char *src, const char *end, char *out) {
    36→    if (src >= end || *src != '\\') {
    37→        return 0;
    38→    }
    39→
    40→    if (src + 1 >= end) {
    41→        *out = '\\';
    42→        return 1;
    43→    }
    44→
    45→    char c = src[1];
    46→    switch (c) {
    47→        case 'a':  *out = '\a'; return 2;
    48→        case 'b':  *out = '\b'; return 2;
    49→        case 'f':  *out = '\f'; return 2;
    50→        case 'n':  *out = '\n'; return 2;
    51→        case 'r':  *out = '\r'; return 2;
    52→        case 't':  *out = '\t'; return 2;
    53→        case 'v':  *out = '\v'; return 2;
    54→        case '\\': *out = '\\'; return 2;
    55→        case '"':  *out = '"';  return 2;
    56→        case '{':  *out = '{';  return 2;
    57→        case '}':  *out = '}';  return 2;
    58→        case '[':  *out = '[';  return 2;
    59→        case ']':  *out = ']';  return 2;
    60→        case '$':  *out = '$';  return 2;
    61→        case '\n':
    62→            /* Backslash-newline: replace with single space, skip following whitespace */
    63→            *out = ' ';
    64→            return 2;  /* Caller should handle skipping whitespace */
    65→        case 'x': {
    66→            /* Hex escape: \xNN (1-2 hex digits) */
    67→            int val = 0;
    68→            int consumed = 2;  /* \x */
    69→            const char *p = src + 2;
