    79→                *out = 'x';
    80→                return 2;
    81→            }
    82→            *out = (char)val;
    83→            return consumed;
    84→        }
    85→        case 'u': {
    86→            /* Unicode escape: \uNNNN (4 hex digits) */
    87→            if (src + 5 < end) {
