   347→                        break;
   348→                    }
   349→
   350→                    /* Parse argument specification */
   351→                    TclObj **argSpecs = NULL;
   352→                    size_t argCount = 0;
   353→                    if (host->asList(argList, &argSpecs, &argCount) != 0) {
   354→                        tclSetError(interp, "invalid argument list", -1);
   355→                        result = TCL_ERROR;
   356→                        break;
   357→                    }
   358→
   359→                    /* Create new frame for proc execution */
   360→                    TclFrame *procFrame = host->frameAlloc(interp->hostCtx);
   361→                    if (!procFrame) {
   362→                        tclSetError(interp, "out of memory", -1);
   363→                        result = TCL_ERROR;
   364→                        break;
   365→                    }
   366→                    procFrame->parent = interp->currentFrame;
   367→                    procFrame->level = interp->currentFrame->level + 1;
   368→                    procFrame->flags = TCL_FRAME_PROC;
   369→
   370→                    /* Get proc name for stack traces */
   371→                    size_t procNameLen;
   372→                    procFrame->procName = host->getStringPtr(state->substWords[0], &procNameLen);
   373→
   374→                    /* Store invocation for info level */
   375→                    procFrame->invocationObjs = state->substWords;
   376→                    procFrame->invocationCount = state->substCount;
   377→
   378→                    /* Bind arguments to local variables */
   379→                    int actualArgs = state->substCount - 1;  /* Exclude command name */
   380→                    int hasArgs = 0;  /* Whether last param is 'args' */
   381→
   382→                    /* Check if last arg is 'args' for varargs */
   383→                    if (argCount > 0) {
   384→                        size_t lastArgLen;
   385→                        const char *lastArg = host->getStringPtr(argSpecs[argCount - 1], &lastArgLen);
   386→                        if (lastArgLen == 4 && tclStrncmp(lastArg, "args", 4) == 0) {
   387→                            hasArgs = 1;
   388→                        }
   389→                    }
   390→
   391→                    /* Check argument count */
   392→                    int requiredArgs = (int)argCount - (hasArgs ? 1 : 0);
   393→
   394→                    /* Handle default arguments - count how many have defaults */
   395→                    int minArgs = 0;
   396→                    for (size_t i = 0; i < (size_t)requiredArgs; i++) {
   397→                        /* Check if arg has a default value (is a 2-element list) */
   398→                        size_t listLen = host->listLength(argSpecs[i]);
   399→                        if (listLen < 2) {
   400→                            minArgs++;  /* Required argument */
   401→                        }
   402→                    }
   403→
   404→                    if (actualArgs < minArgs || (!hasArgs && actualArgs > requiredArgs)) {
   405→                        tclSetError(interp, "wrong # args", -1);
   406→                        host->frameFree(interp->hostCtx, procFrame);
   407→                        result = TCL_ERROR;
   408→                        break;
   409→                    }
   410→
   411→                    /* Bind each argument */
   412→                    for (size_t i = 0; i < (size_t)requiredArgs; i++) {
   413→                        TclObj *argSpec = argSpecs[i];
   414→                        size_t argNameLen;
   415→                        const char *argName;
   416→                        TclObj *value = NULL;
   417→
   418→                        /* Check if arg has a default */
   419→                        size_t listLen = host->listLength(argSpec);
   420→                        if (listLen >= 2) {
