     1→/*
     2→ * builtin_coroutine.c - TCL Coroutine Command Implementations
     3→ *
     4→ * Coroutine commands: coroutine, yield, yieldto
     5→ *
     6→ * Coroutines allow suspending and resuming execution. When a coroutine
     7→ * yields, it saves its state and returns to the caller. When resumed,
     8→ * it continues from where it left off.
     9→ */
    10→
    11→#include "internal.h"
    12→#include <string.h>
    13→
    14→/* ========================================================================
    15→ * Coroutine State
    16→ * ======================================================================== */
    17→
    18→typedef struct TclCoroutine {
    19→    char       *name;           /* Coroutine name (fully qualified) */
    20→    size_t      nameLen;        /* Length of name */
    21→    TclFrame   *savedFrame;     /* Saved frame for coroutine */
    22→    TclFrame   *baseFrame;      /* Frame where coroutine was created */
    23→    int         running;        /* Currently executing */
    24→    int         done;           /* Finished (returned or errored) */
    25→    TclObj     *result;         /* Last result/yield value */
    26→    TclInterp  *interp;         /* Interpreter */
    27→
    28→    /* For initial invocation */
    29→    TclObj    **cmdObjs;        /* Command + args to execute */
    30→    int         cmdObjc;        /* Count of command objects */
    31→    int         started;        /* Has first execution started? */
    32→
    33→    /* For script execution and resumption using yield counting */
    34→    TclObj     *scriptObj;      /* Script being executed (proc body) - enables AST caching */
    35→    int         yieldCount;     /* Number of yields executed so far */
    36→    int         yieldTarget;    /* Target yield to stop at on resume */
    37→    TclObj     *resumeValue;    /* Value to return from yield on resume */
    38→
    39→    /* Loop state stack for proper suspend/resume inside loops */
    40→    TclLoopState *currentLoop;  /* Top of loop state stack */
    41→} TclCoroutine;
    42→
    43→/* Maximum number of active coroutines */
    44→#define MAX_COROUTINES 64
    45→static TclCoroutine *gCoroutines[MAX_COROUTINES];
    46→static int gCoroutineCount = 0;
    47→
    48→/* Current coroutine being executed (for yield to find) */
    49→static TclCoroutine *gCurrentCoroutine = NULL;
    50→
    51→/* Global yield flag - set by yield, checked by eval loop */
    52→static int gYieldPending = 0;
    53→
    54→/* Global yield offset - position in script where yield occurred */
    55→static size_t gYieldOffset = 0;
    56→
    57→/* Check if yield is pending (called by eval loop) */
    58→int tclCoroYieldPending(void) {
    59→    return gYieldPending;
    60→}
    61→
    62→/* Clear yield pending flag */
    63→void tclCoroClearYield(void) {
    64→    gYieldPending = 0;
    65→}
    66→
    67→/* Set yield offset (called by eval.c when yield is detected) */
    68→void tclCoroSetYieldOffset(size_t offset) {
    69→    gYieldOffset = offset;
    70→}
    71→
    72→/* Get yield offset (called by control structures to save resume position) */
    73→size_t tclCoroGetYieldOffset(void) {
    74→    return gYieldOffset;
    75→}
    76→
    77→/* Get current coroutine (for loop state management) */
    78→TclCoroutine *tclCoroGetCurrent(void) {
    79→    return gCurrentCoroutine;
    80→}
    81→
    82→/* ========================================================================
    83→ * Loop State Management
    84→ * ======================================================================== */
    85→
    86→/* Push a new loop state onto the current coroutine's loop stack */
    87→TclLoopState *tclCoroLoopPush(TclLoopType type) {
    88→    if (!gCurrentCoroutine) return NULL;
    89→
    90→    TclInterp *interp = gCurrentCoroutine->interp;
    91→    const TclHost *host = interp->host;
    92→
    93→    /* Allocate from arena - will be freed when coroutine ends */
    94→    void *arena = host->arenaPush(interp->hostCtx);
    95→    TclLoopState *loop = host->arenaAlloc(arena, sizeof(TclLoopState), sizeof(void*));
    96→    if (!loop) {
    97→        host->arenaPop(interp->hostCtx, arena);
    98→        return NULL;
    99→    }
   100→
   101→    /* Initialize */
   102→    loop->type = type;
   103→    loop->phase = LOOP_PHASE_TEST;
   104→    loop->bodyObj = NULL;
   105→    loop->bodyResumeOffset = 0;
   106→    loop->testObj = NULL;
   107→    loop->nextObj = NULL;
   108→    loop->elems = NULL;
   109→    loop->elemCount = 0;
   110→    loop->currentIndex = 0;
   111→    loop->varName = NULL;
   112→    loop->varNameLen = 0;
   113→
   114→    /* Link to parent */
   115→    loop->parent = gCurrentCoroutine->currentLoop;
   116→    gCurrentCoroutine->currentLoop = loop;
   117→
   118→    return loop;
   119→}
   120→
   121→/* Pop the top loop state from the current coroutine's loop stack */
   122→void tclCoroLoopPop(void) {
   123→    if (!gCurrentCoroutine || !gCurrentCoroutine->currentLoop) return;
   124→
   125→    TclLoopState *loop = gCurrentCoroutine->currentLoop;
   126→    gCurrentCoroutine->currentLoop = loop->parent;
   127→    /* Note: loop memory will be freed when coroutine's arena is popped */
   128→}
   129→
   130→/* Get the current loop state (top of stack) */
   131→TclLoopState *tclCoroLoopCurrent(void) {
   132→    if (!gCurrentCoroutine) return NULL;
   133→    return gCurrentCoroutine->currentLoop;
   134→}
   135→
   136→/* ========================================================================
   137→ * Coroutine Management
   138→ * ======================================================================== */
   139→
   140→static TclCoroutine *coroFind(const char *name, size_t len) {
   141→    for (int i = 0; i < gCoroutineCount; i++) {
   142→        if (gCoroutines[i] && gCoroutines[i]->nameLen == len &&
   143→            tclStrncmp(gCoroutines[i]->name, name, len) == 0) {
   144→            return gCoroutines[i];
   145→        }
   146→    }
   147→    return NULL;
   148→}
   149→
   150→static TclCoroutine *coroFindByFullName(const char *fullName, size_t len) {
   151→    /* Try exact match first */
   152→    TclCoroutine *c = coroFind(fullName, len);
   153→    if (c) return c;
   154→
   155→    /* Try with :: prefix */
   156→    if (len >= 2 && fullName[0] == ':' && fullName[1] == ':') {
   157→        return coroFind(fullName + 2, len - 2);
   158→    }
   159→
   160→    /* Try adding :: prefix */
   161→    for (int i = 0; i < gCoroutineCount; i++) {
   162→        if (gCoroutines[i] && gCoroutines[i]->nameLen == len + 2 &&
   163→            gCoroutines[i]->name[0] == ':' && gCoroutines[i]->name[1] == ':' &&
   164→            tclStrncmp(gCoroutines[i]->name + 2, fullName, len) == 0) {
   165→            return gCoroutines[i];
   166→        }
   167→    }
   168→    return NULL;
   169→}
   170→
   171→static void coroFree(TclCoroutine *coro) {
   172→    if (!coro) return;
   173→
   174→    /* Remove from global list */
   175→    for (int i = 0; i < gCoroutineCount; i++) {
   176→        if (gCoroutines[i] == coro) {
   177→            gCoroutines[i] = gCoroutines[gCoroutineCount - 1];
   178→            gCoroutines[gCoroutineCount - 1] = NULL;
   179→            gCoroutineCount--;
   180→            break;
   181→        }
   182→    }
   183→
   184→    /* Note: we don't free the name/cmdObjs here as they're arena-allocated
   185→     * or managed by the interpreter. The coroutine struct itself is also
   186→     * arena-allocated in a real implementation. For now we leak. */
   187→}
   188→
   189→static TclCoroutine *coroCreate(TclInterp *interp, const char *name, size_t nameLen) {
   190→    if (gCoroutineCount >= MAX_COROUTINES) {
   191→        return NULL;
   192→    }
   193→
   194→    const TclHost *host = interp->host;
   195→    void *arena = host->arenaPush(interp->hostCtx);
   196→
   197→    TclCoroutine *coro = host->arenaAlloc(arena, sizeof(TclCoroutine), sizeof(void*));
   198→    if (!coro) {
   199→        host->arenaPop(interp->hostCtx, arena);
   200→        return NULL;
   201→    }
   202→
   203→    /* Build fully qualified name */
   204→    char *fullName;
   205→    size_t fullLen;
   206→    if (nameLen >= 2 && name[0] == ':' && name[1] == ':') {
   207→        fullName = host->arenaStrdup(arena, name, nameLen);
   208→        fullLen = nameLen;
   209→    } else {
   210→        fullName = host->arenaAlloc(arena, nameLen + 3, 1);
   211→        fullName[0] = ':';
   212→        fullName[1] = ':';
   213→        for (size_t i = 0; i < nameLen; i++) {
   214→            fullName[i + 2] = name[i];
   215→        }
   216→        fullName[nameLen + 2] = '\0';
   217→        fullLen = nameLen + 2;
   218→    }
   219→
   220→    coro->name = fullName;
   221→    coro->nameLen = fullLen;
   222→    coro->savedFrame = NULL;
   223→    coro->baseFrame = interp->currentFrame;
   224→    coro->running = 0;
   225→    coro->done = 0;
   226→    coro->result = NULL;
   227→    coro->interp = interp;
   228→    coro->cmdObjs = NULL;
   229→    coro->cmdObjc = 0;
   230→    coro->started = 0;
   231→    coro->scriptObj = NULL;
   232→    coro->yieldCount = 0;
   233→    coro->yieldTarget = 0;
   234→    coro->resumeValue = NULL;
   235→    coro->currentLoop = NULL;
   236→
   237→    gCoroutines[gCoroutineCount++] = coro;
   238→
   239→    /* Note: arena is intentionally not popped - coro needs to persist */
   240→
   241→    return coro;
   242→}
   243→
   244→/* ========================================================================
   245→ * Coroutine Execution
   246→ * ======================================================================== */
   247→
   248→/* Evaluate script within coroutine context, using yield counting for resume */
   249→static TclResult coroEvalScript(TclCoroutine *coro) {
   250→    TclInterp *interp = coro->interp;
   251→
   252→    /* Always execute from the beginning - yield counting handles resume.
   253→     * Using tclEvalObj enables AST caching for repeated evaluations. */
   254→    TclResult result = tclEvalObj(interp, coro->scriptObj, 0);
   255→
   256→    return result;
   257→}
   258→
   259→/* Execute the coroutine's command (first invocation or resume) */
   260→static TclResult coroExecute(TclCoroutine *coro, TclObj *resumeValue) {
   261→    TclInterp *interp = coro->interp;
   262→    const TclHost *host = interp->host;
   263→
   264→    if (coro->done) {
   265→        tclSetError(interp, "invalid command name", -1);
   266→        return TCL_ERROR;
   267→    }
   268→
   269→    if (coro->running) {
   270→        tclSetError(interp, "coroutine is already running", -1);
   271→        return TCL_ERROR;
   272→    }
   273→
   274→    coro->running = 1;
   275→    coro->resumeValue = resumeValue;
   276→    TclCoroutine *prevCoro = gCurrentCoroutine;
   277→    gCurrentCoroutine = coro;
   278→    gYieldPending = 0;
   279→
   280→    TclResult result;
   281→    TclFrame *savedFrame = interp->currentFrame;
   282→
   283→    if (!coro->started) {
   284→        /* First invocation - set up the coroutine */
   285→        coro->started = 1;
   286→
   287→        if (coro->cmdObjc < 1) {
   288→            coro->running = 0;
   289→            gCurrentCoroutine = prevCoro;
   290→            tclSetError(interp, "no command to execute", -1);
   291→            return TCL_ERROR;
   292→        }
   293→
   294→        /* Create the coroutine frame */
   295→        TclFrame *coroFrame = host->frameAlloc(interp->hostCtx);
   296→        if (!coroFrame) {
   297→            coro->running = 0;
   298→            gCurrentCoroutine = prevCoro;
   299→            tclSetError(interp, "out of memory", -1);
   300→            return TCL_ERROR;
   301→        }
   302→        coroFrame->parent = interp->globalFrame;
   303→        coroFrame->level = 1;
   304→        coroFrame->flags = TCL_FRAME_PROC | TCL_FRAME_COROUTINE;
   305→        coroFrame->procName = coro->name;
   306→        coroFrame->invocationObjs = coro->cmdObjs;
   307→        coroFrame->invocationCount = coro->cmdObjc;
   308→
   309→        coro->savedFrame = coroFrame;
   310→
   311→        /* Look up the command */
   312→        size_t cmdLen;
   313→        const char *cmdName = host->getStringPtr(coro->cmdObjs[0], &cmdLen);
   314→
   315→        int builtinIdx = tclBuiltinLookup(cmdName, cmdLen);
   316→        TclCmdInfo cmdInfo;
   317→
   318→        if (builtinIdx >= 0) {
   319→            cmdInfo.type = TCL_CMD_BUILTIN;
   320→            cmdInfo.u.builtinId = builtinIdx;
   321→        } else if (host->cmdLookup(interp->hostCtx, cmdName, cmdLen, &cmdInfo) != 0) {
   322→            cmdInfo.type = TCL_CMD_NOT_FOUND;
   323→        }
   324→
   325→        if (cmdInfo.type == TCL_CMD_PROC) {
   326→            /* Get proc definition and save script */
   327→            TclObj *argList = NULL;
   328→            TclObj *body = NULL;
   329→            if (host->procGetDef(cmdInfo.u.procHandle, &argList, &body) != 0) {
   330→                coro->running = 0;
   331→                gCurrentCoroutine = prevCoro;
   332→                host->frameFree(interp->hostCtx, coroFrame);
   333→                tclSetError(interp, "proc definition not found", -1);
   334→                return TCL_ERROR;
   335→            }
   336→
   337→            /* Parse and bind arguments */
   338→            TclObj **argSpecs = NULL;
   339→            size_t argCount = 0;
   340→            host->asList(argList, &argSpecs, &argCount);
   341→
   342→            int actualArgs = coro->cmdObjc - 1;
   343→            int hasArgs = 0;
   344→
   345→            if (argCount > 0) {
   346→                size_t lastArgLen;
   347→                const char *lastArg = host->getStringPtr(argSpecs[argCount - 1], &lastArgLen);
   348→                if (lastArgLen == 4 && tclStrncmp(lastArg, "args", 4) == 0) {
   349→                    hasArgs = 1;
   350→                }
   351→            }
   352→
   353→            int requiredArgs = (int)argCount - (hasArgs ? 1 : 0);
   354→
   355→            /* Bind arguments to frame */
   356→            interp->currentFrame = coroFrame;
   357→            for (size_t i = 0; i < (size_t)requiredArgs; i++) {
   358→                TclObj *argSpec = argSpecs[i];
   359→                size_t argNameLen;
   360→                const char *argName;
   361→                TclObj *value = NULL;
   362→
   363→                size_t listLen = host->listLength(argSpec);
   364→                if (listLen >= 2) {
   365→                    TclObj *nameObj = host->listIndex(argSpec, 0);
   366→                    argName = host->getStringPtr(nameObj, &argNameLen);
   367→                    if ((int)i < actualArgs) {
   368→                        value = coro->cmdObjs[i + 1];
   369→                    } else {
   370→                        value = host->listIndex(argSpec, 1);
   371→                    }
   372→                } else {
   373→                    argName = host->getStringPtr(argSpec, &argNameLen);
   374→                    if ((int)i < actualArgs) {
   375→                        value = coro->cmdObjs[i + 1];
   376→                    }
   377→                }
   378→
   379→                if (value) {
   380→                    host->varSet(coroFrame->varsHandle, argName, argNameLen, host->dup(value));
   381→                }
   382→            }
   383→
   384→            if (hasArgs) {
   385→                int argsStart = requiredArgs;
   386→                int argsCount = actualArgs - argsStart;
   387→                if (argsCount < 0) argsCount = 0;
   388→
   389→                TclObj *argsList;
   390→                if (argsCount > 0) {
   391→                    argsList = host->newList(&coro->cmdObjs[argsStart + 1], argsCount);
   392→                } else {
   393→                    argsList = host->newString("", 0);
   394→                }
   395→                host->varSet(coroFrame->varsHandle, "args", 4, argsList);
   396→            }
   397→
   398→            /* Save script for resumption - keep TclObj to enable AST caching */
   399→            coro->scriptObj = host->dup(body);
   400→
   401→            /* Execute with yield counting support */
   402→            result = coroEvalScript(coro);
   403→        } else if (cmdInfo.type == TCL_CMD_BUILTIN) {
   404→            interp->currentFrame = coroFrame;
   405→            const TclBuiltinEntry *entry = tclBuiltinGet(cmdInfo.u.builtinId);
   406→            if (entry) {
   407→                result = entry->proc(interp, coro->cmdObjc, coro->cmdObjs);
   408→            } else {
   409→                result = TCL_ERROR;
   410→                tclSetError(interp, "invalid builtin", -1);
   411→            }
   412→        } else {
   413→            coro->running = 0;
   414→            gCurrentCoroutine = prevCoro;
   415→            host->frameFree(interp->hostCtx, coroFrame);
   416→            tclSetError(interp, "invalid command name", -1);
   417→            return TCL_ERROR;
   418→        }
   419→    } else {
   420→        /* Resume - re-execute script with yield counting */
   421→        interp->currentFrame = coro->savedFrame;
   422→
   423→        /* Set yieldTarget to skip to where we were */
   424→        coro->yieldTarget = coro->yieldCount;
   425→        coro->yieldCount = 0;
   426→
   427→        if (coro->scriptObj) {
   428→            result = coroEvalScript(coro);
   429→        } else {
   430→            result = TCL_OK;
   431→        }
   432→    }
   433→
   434→    interp->currentFrame = savedFrame;
   435→
   436→    /* Check if we yielded or finished */
   437→    if (gYieldPending) {
   438→        /* Yielded - keep coroutine alive */
   439→        gYieldPending = 0;
   440→        coro->running = 0;
   441→        gCurrentCoroutine = prevCoro;
   442→        tclSetResult(interp, coro->result);
   443→        return TCL_OK;
   444→    }
   445→
   446→    /* Finished - clean up */
   447→    coro->done = 1;
   448→    coro->running = 0;
   449→    gCurrentCoroutine = prevCoro;
   450→
   451→    if (result == TCL_RETURN) {
   452→        result = TCL_OK;
   453→    }
   454→    if (result == TCL_OK) {
   455→        coro->result = interp->result;
   456→    }
   457→
   458→    /* Free the coroutine frame */
   459→    if (coro->savedFrame) {
   460→        host->frameFree(interp->hostCtx, coro->savedFrame);
   461→        coro->savedFrame = NULL;
   462→    }
   463→
   464→    tclSetResult(interp, coro->result ? coro->result : host->newString("", 0));
   465→    return result;
   466→}
   467→
   468→/* ========================================================================
   469→ * coroutine Command
   470→ *
   471→ * coroutine name command ?arg ...?
   472→ *
   473→ * Creates a new coroutine with the given name and starts executing
   474→ * the command. Returns the first yielded value or the final result.
   475→ * ======================================================================== */
   476→
   477→TclResult tclCmdCoroutine(TclInterp *interp, int objc, TclObj **objv) {
   478→    const TclHost *host = interp->host;
   479→
   480→    if (objc < 3) {
   481→        tclSetError(interp, "wrong # args: should be \"coroutine name command ?arg ...?\"", -1);
   482→        return TCL_ERROR;
   483→    }
   484→
   485→    size_t nameLen;
   486→    const char *name = host->getStringPtr(objv[1], &nameLen);
   487→
   488→    /* Check if a coroutine with this name already exists */
   489→    if (coroFindByFullName(name, nameLen)) {
   490→        tclSetError(interp, "command already exists", -1);
   491→        return TCL_ERROR;
   492→    }
   493→
   494→    /* Create the coroutine */
   495→    TclCoroutine *coro = coroCreate(interp, name, nameLen);
   496→    if (!coro) {
   497→        tclSetError(interp, "cannot create coroutine", -1);
   498→        return TCL_ERROR;
   499→    }
   500→
   501→    /* Store the command to execute */
   502→    coro->cmdObjs = &objv[2];
   503→    coro->cmdObjc = objc - 2;
   504→
   505→    /* Execute the coroutine (starts the command) */
   506→    TclResult result = coroExecute(coro, NULL);
   507→
   508→    if (result != TCL_OK && !coro->done) {
   509→        /* Error during initial execution */
   510→        coroFree(coro);
   511→        return result;
   512→    }
   513→
   514→    /* Return the first yield value or final result */
   515→    if (coro->result) {
   516→        tclSetResult(interp, coro->result);
   517→    } else {
   518→        tclSetResult(interp, host->newString("", 0));
   519→    }
   520→
   521→    /* If coroutine finished immediately, clean up */
   522→    if (coro->done) {
   523→        coroFree(coro);
   524→    }
   525→
   526→    return TCL_OK;
   527→}
   528→
   529→/* ========================================================================
   530→ * yield Command
   531→ *
   532→ * yield ?value?
   533→ *
   534→ * Suspends the current coroutine and returns value to the caller.
   535→ * When resumed, returns the value passed by the caller.
   536→ * ======================================================================== */
   537→
   538→TclResult tclCmdYield(TclInterp *interp, int objc, TclObj **objv) {
   539→    const TclHost *host = interp->host;
   540→
   541→    if (objc > 2) {
   542→        tclSetError(interp, "wrong # args: should be \"yield ?value?\"", -1);
   543→        return TCL_ERROR;
   544→    }
   545→
   546→    /* Check if we're in a coroutine */
   547→    if (!gCurrentCoroutine) {
   548→        tclSetError(interp, "yield can only be called inside a coroutine", -1);
   549→        return TCL_ERROR;
   550→    }
   551→
   552→    TclCoroutine *coro = gCurrentCoroutine;
   553→
   554→    /* Check if we should skip this yield (replaying to catch up) */
   555→    if (coro->yieldCount < coro->yieldTarget) {
   556→        /* Skip this yield - return the resume value instead */
   557→        coro->yieldCount++;
   558→        tclSetResult(interp, coro->resumeValue ? coro->resumeValue : host->newString("", 0));
   559→        return TCL_OK;
   560→    }
   561→
   562→    /* Get the yield value */
   563→    TclObj *value;
   564→    if (objc == 2) {
   565→        value = objv[1];
   566→    } else {
   567→        value = host->newString("", 0);
   568→    }
   569→
   570→    /* Actually yield - increment count and signal */
   571→    coro->yieldCount++;
   572→    coro->result = value;
   573→    coro->running = 0;
   574→    gYieldPending = 1;
   575→
   576→    /* Return the value - this will be the result of the coroutine call */
   577→    tclSetResult(interp, value);
   578→
   579→    return TCL_OK;
   580→}
   581→
   582→/* ========================================================================
   583→ * yieldto Command
   584→ *
   585→ * yieldto command ?arg ...?
   586→ *
   587→ * Suspends the current coroutine and calls command. The return value
   588→ * of command becomes the result returned to the coroutine caller.
   589→ * When the coroutine is resumed, the arguments passed become yield's
   590→ * return value.
   591→ * ======================================================================== */
   592→
   593→TclResult tclCmdYieldto(TclInterp *interp, int objc, TclObj **objv) {
   594→    const TclHost *host = interp->host;
   595→
   596→    if (objc < 2) {
   597→        tclSetError(interp, "wrong # args: should be \"yieldto command ?arg ...?\"", -1);
   598→        return TCL_ERROR;
   599→    }
   600→
   601→    /* Check if we're in a coroutine */
   602→    if (!gCurrentCoroutine) {
   603→        tclSetError(interp, "yieldto can only be called inside a coroutine", -1);
   604→        return TCL_ERROR;
   605→    }
   606→
   607→    /* Execute the command */
   608→    size_t cmdLen;
   609→    const char *cmdName = host->getStringPtr(objv[1], &cmdLen);
   610→
   611→    /* Look up command */
   612→    int builtinIdx = tclBuiltinLookup(cmdName, cmdLen);
   613→    TclCmdInfo cmdInfo;
   614→
   615→    if (builtinIdx >= 0) {
   616→        cmdInfo.type = TCL_CMD_BUILTIN;
   617→        cmdInfo.u.builtinId = builtinIdx;
   618→    } else if (host->cmdLookup(interp->hostCtx, cmdName, cmdLen, &cmdInfo) != 0) {
   619→        cmdInfo.type = TCL_CMD_NOT_FOUND;
   620→    }
   621→
   622→    TclResult result;
   623→
   624→    switch (cmdInfo.type) {
   625→        case TCL_CMD_BUILTIN: {
   626→            const TclBuiltinEntry *entry = tclBuiltinGet(cmdInfo.u.builtinId);
   627→            if (entry) {
   628→                result = entry->proc(interp, objc - 1, &objv[1]);
   629→            } else {
   630→                tclSetError(interp, "invalid builtin", -1);
   631→                result = TCL_ERROR;
   632→            }
   633→            break;
   634→        }
   635→        case TCL_CMD_PROC: {
   636→            TclObj *argList = NULL;
   637→            TclObj *body = NULL;
   638→            if (host->procGetDef(cmdInfo.u.procHandle, &argList, &body) != 0) {
   639→                tclSetError(interp, "proc definition not found", -1);
   640→                result = TCL_ERROR;
   641→                break;
   642→            }
   643→            result = tclEvalObj(interp, body, 0);
   644→            break;
   645→        }
   646→        case TCL_CMD_EXTENSION:
   647→            result = host->extInvoke(interp, cmdInfo.u.extHandle, objc - 1, &objv[1]);
   648→            break;
   649→        default:
   650→            tclSetError(interp, "invalid command name", -1);
   651→            result = TCL_ERROR;
   652→            break;
   653→    }
   654→
   655→    if (result != TCL_OK) {
   656→        return result;
   657→    }
   658→
   659→    TclCoroutine *coro = gCurrentCoroutine;
   660→
   661→    /* Check if we should skip this yield (replaying to catch up) */
   662→    if (coro->yieldCount < coro->yieldTarget) {
   663→        /* Skip this yield - return the resume value instead */
   664→        coro->yieldCount++;
   665→        tclSetResult(interp, coro->resumeValue ? coro->resumeValue : host->newString("", 0));
   666→        return TCL_OK;
   667→    }
   668→
   669→    /* Actually yield - increment count and signal */
   670→    coro->yieldCount++;
   671→    coro->result = interp->result;
   672→    coro->running = 0;
   673→    gYieldPending = 1;
   674→
   675→    return TCL_OK;
   676→}
   677→
   678→/* ========================================================================
   679→ * Coroutine Invocation (when calling the coroutine by name)
   680→ *
   681→ * This is called when the coroutine command is invoked to resume it.
   682→ * ======================================================================== */
   683→
   684→TclResult tclCoroInvoke(TclInterp *interp, TclCoroutine *coro, int objc, TclObj **objv) {
   685→    const TclHost *host = interp->host;
   686→
   687→    if (coro->done) {
   688→        /* Build error message */
   689→        void *arena = host->arenaPush(interp->hostCtx);
   690→        char *msg = host->arenaAlloc(arena, coro->nameLen + 30, 1);
   691→        char *p = msg;
   692→        const char *prefix = "invalid command name \"";
   693→        while (*prefix) *p++ = *prefix++;
   694→        /* Use short name (without ::) */
   695→        const char *shortName = coro->name;
   696→        size_t shortLen = coro->nameLen;
   697→        if (shortLen >= 2 && shortName[0] == ':' && shortName[1] == ':') {
   698→            shortName += 2;
   699→            shortLen -= 2;
   700→        }
   701→        for (size_t i = 0; i < shortLen; i++) *p++ = shortName[i];
   702→        *p++ = '"';
   703→        *p = '\0';
   704→        tclSetError(interp, msg, p - msg);
   705→        host->arenaPop(interp->hostCtx, arena);
   706→        return TCL_ERROR;
   707→    }
   708→
   709→    /* Get resume value (arguments passed to coroutine) */
   710→    TclObj *resumeValue = NULL;
   711→    if (objc > 1) {
   712→        /* Concatenate all arguments into a single value */
   713→        if (objc == 2) {
   714→            resumeValue = objv[1];
   715→        } else {
   716→            /* Create a list of all args */
   717→            resumeValue = host->newList(&objv[1], objc - 1);
   718→        }
   719→    }
   720→
   721→    /* Resume the coroutine */
   722→    return coroExecute(coro, resumeValue);
   723→}
   724→
   725→/* ========================================================================
   726→ * Lookup function for coroutine commands
   727→ * ======================================================================== */
   728→
   729→TclCoroutine *tclCoroLookup(const char *name, size_t len) {
   730→    return coroFindByFullName(name, len);
   731→}
   732→
   733→/* ========================================================================
   734→ * Get current coroutine name (for info coroutine)
   735→ * ======================================================================== */
   736→
   737→const char *tclCoroCurrentName(size_t *lenOut) {
   738→    if (!gCurrentCoroutine) {
   739→        if (lenOut) *lenOut = 0;
   740→        return "";
   741→    }
   742→    if (lenOut) *lenOut = gCurrentCoroutine->nameLen;
   743→    return gCurrentCoroutine->name;
   744→}
   745→

