     1→/*
     2→ * builtin_control.c - TCL Control Flow Command Implementations
     3→ *
     4→ * Control flow commands: if, while, for, foreach
     5→ * Also includes the evalExprBool helper function.
     6→ */
     7→
     8→#include "internal.h"
     9→
    10→/* ========================================================================
    11→ * Expression Boolean Evaluation Helper
    12→ * ======================================================================== */
    13→
    14→/* Evaluate expression string and return boolean result */
    15→static int evalExprBool(TclInterp *interp, const char *exprRaw, size_t lenRaw, int *result) {
    16→    const TclHost *host = interp->host;
    17→
    18→    /* First, perform variable/command substitution on the expression */
    19→    TclObj *substResult = tclSubstString(interp, exprRaw, lenRaw, TCL_SUBST_ALL);
    20→    if (!substResult) {
    21→        return -1;
    22→    }
    23→
    24→    size_t len;
    25→    const char *expr = host->getStringPtr(substResult, &len);
    26→
    27→    /* Simple expression evaluator - handles:
    28→     * - Integer literals (0 = false, non-zero = true)
    29→     * - Comparisons: ==, !=, <, >, <=, >=
    30→     * - Boolean literals: true, false, yes, no, on, off
    31→     */
    32→    const char *p = expr;
    33→    const char *end = expr + len;
    34→
    35→    /* Skip whitespace */
    36→    while (p < end && (*p == ' ' || *p == '\t')) p++;
    37→    if (p >= end) {
    38→        *result = 0;
    39→        return 0;
    40→    }
    41→
    42→    /* Check for boolean literals */
    43→    size_t remaining = end - p;
    44→    if (remaining == 4 && tclStrncmp(p, "true", 4) == 0) { *result = 1; return 0; }
    45→    if (remaining == 5 && tclStrncmp(p, "false", 5) == 0) { *result = 0; return 0; }
    46→    if (remaining == 3 && tclStrncmp(p, "yes", 3) == 0) { *result = 1; return 0; }
    47→    if (remaining == 2 && tclStrncmp(p, "no", 2) == 0) { *result = 0; return 0; }
    48→    if (remaining == 2 && tclStrncmp(p, "on", 2) == 0) { *result = 1; return 0; }
    49→    if (remaining == 3 && tclStrncmp(p, "off", 3) == 0) { *result = 0; return 0; }
    50→
    51→    /* Try to parse as simple comparison or arithmetic */
    52→    /* First, look for comparison operators */
    53→    const char *op = NULL;
    54→    int opLen = 0;
    55→    const char *scan = p;
    56→    while (scan < end) {
    57→        if (scan + 1 < end) {
    58→            if ((scan[0] == '=' && scan[1] == '=') ||
    59→                (scan[0] == '!' && scan[1] == '=') ||
    60→                (scan[0] == '<' && scan[1] == '=') ||
    61→                (scan[0] == '>' && scan[1] == '=')) {
    62→                op = scan;
    63→                opLen = 2;
    64→                break;
    65→            }
    66→        }
    67→        if (*scan == '<' || *scan == '>') {
    68→            op = scan;
    69→            opLen = 1;
    70→            break;
    71→        }
    72→        scan++;
    73→    }
    74→
    75→    if (op) {
    76→        /* Comparison expression */
    77→        /* Parse left operand */
    78→        const char *leftStart = p;
    79→        while (leftStart < op && (*leftStart == ' ' || *leftStart == '\t')) leftStart++;
    80→        const char *leftEnd = op;
    81→        while (leftEnd > leftStart && (leftEnd[-1] == ' ' || leftEnd[-1] == '\t')) leftEnd--;
    82→
    83→        /* Strip quotes from left operand */
    84→        if (leftEnd > leftStart && *leftStart == '"' && leftEnd[-1] == '"') {
    85→            leftStart++;
    86→            leftEnd--;
    87→        }
    88→
    89→        /* Parse right operand */
    90→        const char *rightStart = op + opLen;
    91→        while (rightStart < end && (*rightStart == ' ' || *rightStart == '\t')) rightStart++;
    92→        const char *rightEnd = end;
    93→        while (rightEnd > rightStart && (rightEnd[-1] == ' ' || rightEnd[-1] == '\t')) rightEnd--;
    94→
    95→        /* Strip quotes from right operand */
    96→        if (rightEnd > rightStart && *rightStart == '"' && rightEnd[-1] == '"') {
    97→            rightStart++;
    98→            rightEnd--;
    99→        }
   100→
   101→        /* Try as integers first */
   102→        int64_t leftVal = 0, rightVal = 0;
   103→        int leftIsInt = 1, rightIsInt = 1;
   104→
   105→        /* Parse left as int */
   106→        const char *lp = leftStart;
   107→        int lneg = 0;
   108→        if (lp < leftEnd && *lp == '-') { lneg = 1; lp++; }
   109→        else if (lp < leftEnd && *lp == '+') { lp++; }
   110→        if (lp < leftEnd && *lp >= '0' && *lp <= '9') {
   111→            while (lp < leftEnd && *lp >= '0' && *lp <= '9') {
   112→                leftVal = leftVal * 10 + (*lp - '0');
   113→                lp++;
   114→            }
   115→            if (lneg) leftVal = -leftVal;
   116→            /* Check for trailing non-digits */
   117→            while (lp < leftEnd && (*lp == ' ' || *lp == '\t')) lp++;
   118→            if (lp != leftEnd) leftIsInt = 0;
   119→        } else {
   120→            leftIsInt = 0;
   121→        }
   122→
   123→        /* Parse right as int */
   124→        const char *rp = rightStart;
   125→        int rneg = 0;
   126→        if (rp < rightEnd && *rp == '-') { rneg = 1; rp++; }
   127→        else if (rp < rightEnd && *rp == '+') { rp++; }
   128→        if (rp < rightEnd && *rp >= '0' && *rp <= '9') {
   129→            while (rp < rightEnd && *rp >= '0' && *rp <= '9') {
   130→                rightVal = rightVal * 10 + (*rp - '0');
   131→                rp++;
   132→            }
   133→            if (rneg) rightVal = -rightVal;
   134→            while (rp < rightEnd && (*rp == ' ' || *rp == '\t')) rp++;
   135→            if (rp != rightEnd) rightIsInt = 0;
   136→        } else {
   137→            rightIsInt = 0;
   138→        }
   139→
   140→        if (leftIsInt && rightIsInt) {
   141→            /* Integer comparison */
   142→            if (opLen == 2 && op[0] == '=' && op[1] == '=') {
   143→                *result = (leftVal == rightVal);
   144→            } else if (opLen == 2 && op[0] == '!' && op[1] == '=') {
   145→                *result = (leftVal != rightVal);
   146→            } else if (opLen == 2 && op[0] == '<' && op[1] == '=') {
   147→                *result = (leftVal <= rightVal);
   148→            } else if (opLen == 2 && op[0] == '>' && op[1] == '=') {
   149→                *result = (leftVal >= rightVal);
   150→            } else if (opLen == 1 && op[0] == '<') {
   151→                *result = (leftVal < rightVal);
   152→            } else if (opLen == 1 && op[0] == '>') {
   153→                *result = (leftVal > rightVal);
   154→            }
   155→            return 0;
   156→        } else {
   157→            /* String comparison */
   158→            size_t leftLen = leftEnd - leftStart;
   159→            size_t rightLen = rightEnd - rightStart;
   160→            int cmp = 0;
   161→            size_t minLen = leftLen < rightLen ? leftLen : rightLen;
   162→            cmp = tclStrncmp(leftStart, rightStart, minLen);
   163→            if (cmp == 0) {
   164→                if (leftLen < rightLen) cmp = -1;
   165→                else if (leftLen > rightLen) cmp = 1;
   166→            }
   167→
   168→            if (opLen == 2 && op[0] == '=' && op[1] == '=') {
   169→                *result = (cmp == 0);
   170→            } else if (opLen == 2 && op[0] == '!' && op[1] == '=') {
   171→                *result = (cmp != 0);
   172→            } else if (opLen == 2 && op[0] == '<' && op[1] == '=') {
   173→                *result = (cmp <= 0);
   174→            } else if (opLen == 2 && op[0] == '>' && op[1] == '=') {
   175→                *result = (cmp >= 0);
   176→            } else if (opLen == 1 && op[0] == '<') {
   177→                *result = (cmp < 0);
   178→            } else if (opLen == 1 && op[0] == '>') {
   179→                *result = (cmp > 0);
   180→            }
   181→            return 0;
   182→        }
   183→    }
   184→
   185→    /* Try as simple integer (non-zero = true) */
   186→    int64_t val = 0;
   187→    int neg = 0;
   188→    if (p < end && *p == '-') { neg = 1; p++; }
   189→    else if (p < end && *p == '+') { p++; }
   190→
   191→    if (p < end && *p >= '0' && *p <= '9') {
   192→        while (p < end && *p >= '0' && *p <= '9') {
   193→            val = val * 10 + (*p - '0');
   194→            p++;
   195→        }
   196→        if (neg) val = -val;
   197→        *result = (val != 0);
   198→        return 0;
   199→    }
   200→
   201→    /* Use expr command and interpret result */
   202→    TclObj *exprObj = host->newString(expr, len);
   203→    TclObj *args[2] = { host->newString("expr", 4), exprObj };
   204→    TclResult res = tclCmdExpr(interp, 2, args);
   205→    if (res != TCL_OK) {
   206→        return -1;
   207→    }
   208→
   209→    /* Check result */
   210→    int64_t intResult;
   211→    if (host->asInt(interp->result, &intResult) == 0) {
   212→        *result = (intResult != 0);
   213→        return 0;
   214→    }
   215→
   216→    /* Try as boolean */
   217→    int boolResult;
   218→    if (host->asBool(interp->result, &boolResult) == 0) {
   219→        *result = boolResult;
   220→        return 0;
   221→    }
   222→
   223→    tclSetError(interp, "expected boolean expression", -1);
   224→    return -1;
   225→}
   226→
   227→/* ========================================================================
   228→ * if Command
   229→ * ======================================================================== */
   230→
   231→TclResult tclCmdIf(TclInterp *interp, int objc, TclObj **objv) {
   232→    const TclHost *host = interp->host;
   233→
   234→    if (objc < 3) {
   235→        tclSetError(interp, "wrong # args: should be \"if expr1 ?then? body1 elseif expr2 ?then? body2 ... ?else? ?bodyN?\"", -1);
   236→        return TCL_ERROR;
   237→    }
   238→
   239→    int i = 1;
   240→    while (i < objc) {
   241→        /* Get condition expression */
   242→        size_t exprLen;
   243→        const char *exprStr = host->getStringPtr(objv[i], &exprLen);
   244→        i++;
   245→
   246→        /* Evaluate condition */
   247→        int condResult;
   248→        if (evalExprBool(interp, exprStr, exprLen, &condResult) != 0) {
   249→            return TCL_ERROR;
   250→        }
   251→
   252→        /* Skip optional "then" */
   253→        if (i < objc) {
   254→            size_t kwLen;
   255→            const char *kw = host->getStringPtr(objv[i], &kwLen);
   256→            if (kwLen == 4 && tclStrncmp(kw, "then", 4) == 0) {
   257→                i++;
   258→            }
   259→        }
   260→
   261→        if (i >= objc) {
   262→            tclSetError(interp, "wrong # args: no body after condition", -1);
   263→            return TCL_ERROR;
   264→        }
   265→
   266→        if (condResult) {
   267→            /* Execute this branch */
   268→            return tclEvalObj(interp, objv[i], 0);
   269→        }
   270→
   271→        i++;  /* Skip body */
   272→
   273→        /* Check for elseif or else */
   274→        if (i >= objc) {
   275→            /* No more branches, return empty */
   276→            tclSetResult(interp, host->newString("", 0));
   277→            return TCL_OK;
   278→        }
   279→
   280→        size_t kwLen;
   281→        const char *kw = host->getStringPtr(objv[i], &kwLen);
   282→
   283→        if (kwLen == 6 && tclStrncmp(kw, "elseif", 6) == 0) {
   284→            i++;
   285→            continue;  /* Process next condition */
   286→        }
   287→
   288→        if (kwLen == 4 && tclStrncmp(kw, "else", 4) == 0) {
   289→            i++;
   290→            if (i >= objc) {
   291→                tclSetError(interp, "wrong # args: no body after else", -1);
   292→                return TCL_ERROR;
   293→            }
   294→            /* Execute else branch */
   295→            return tclEvalObj(interp, objv[i], 0);
   296→        }
   297→
   298→        /* Must be else body without "else" keyword */
   299→        return tclEvalObj(interp, objv[i], 0);
   300→    }
   301→
   302→    tclSetResult(interp, host->newString("", 0));
   303→    return TCL_OK;
   304→}
   305→
   306→/* ========================================================================
   307→ * while Command
   308→ *
   309→ * Implemented as a state machine to support coroutine suspend/resume.
   310→ * When a yield occurs inside the loop body, the loop state is preserved
   311→ * and can be resumed on the next coroutine invocation.
   312→ * ======================================================================== */
   313→
   314→TclResult tclCmdWhile(TclInterp *interp, int objc, TclObj **objv) {
   315→    const TclHost *host = interp->host;
   316→
   317→    if (objc != 3) {
   318→        tclSetError(interp, "wrong # args: should be \"while test command\"", -1);
   319→        return TCL_ERROR;
   320→    }
   321→
   322→    /* Get strings for comparison (loop identification) */
   323→    size_t testLen, bodyLen;
   324→    const char *testStr = host->getStringPtr(objv[1], &testLen);
   325→    const char *bodyStr = host->getStringPtr(objv[2], &bodyLen);
   326→
   327→    /* Check if we're resuming an existing while loop in a coroutine */
   328→    TclLoopState *loop = tclCoroLoopCurrent();
   329→
   330→    if (loop && loop->type == LOOP_TYPE_WHILE) {
   331→        /* Verify this is the same while loop by comparing content.
   332→         * When resuming a coroutine, the script is re-parsed so we compare strings. */
   333→        size_t loopTestLen, loopBodyLen;
   334→        const char *loopTestStr = host->getStringPtr(loop->testObj, &loopTestLen);
   335→        const char *loopBodyStr = host->getStringPtr(loop->bodyObj, &loopBodyLen);
   336→        int sameTest = (loopTestLen == testLen &&
   337→                        tclStrncmp(loopTestStr, testStr, testLen) == 0);
   338→        int sameBody = (loopBodyLen == bodyLen &&
   339→                        tclStrncmp(loopBodyStr, bodyStr, bodyLen) == 0);
   340→        if (!sameTest || !sameBody) {
   341→            /* Different while loop - this is a nested one */
   342→            loop = NULL;
   343→        }
   344→        /* Otherwise we're resuming the same loop - use saved state */
   345→    } else {
   346→        loop = NULL;
   347→    }
   348→
   349→    if (!loop) {
   350→        /* Create new loop state (returns NULL if not in coroutine) */
   351→        loop = tclCoroLoopPush(LOOP_TYPE_WHILE);
   352→        if (loop) {
   353→            loop->testObj = host->dup(objv[1]);
   354→            loop->bodyObj = host->dup(objv[2]);
   355→            loop->phase = LOOP_PHASE_TEST;
   356→        }
   357→    }
   358→
   359→    /* If we have loop state, use state machine approach */
   360→    if (loop) {
   361→        while (loop->phase != LOOP_PHASE_DONE) {
   362→            switch (loop->phase) {
   363→                case LOOP_PHASE_TEST: {
   364→                    /* Get test string for expression evaluation */
   365→                    size_t exprLen;
   366→                    const char *exprStr = host->getStringPtr(loop->testObj, &exprLen);
   367→                    int condResult;
   368→                    if (evalExprBool(interp, exprStr, exprLen, &condResult) != 0) {
   369→                        tclCoroLoopPop();
   370→                        return TCL_ERROR;
   371→                    }
   372→                    if (!condResult) {
   373→                        loop->phase = LOOP_PHASE_DONE;
   374→                        break;
   375→                    }
   376→                    loop->phase = LOOP_PHASE_BODY;
   377→                    /* fall through */
   378→                }
   379→                case LOOP_PHASE_BODY: {
   380→                    /* Execute body using tclEvalObj - yield counting handles resume */
   381→                    TclResult result = tclEvalObj(interp, loop->bodyObj, 0);
   382→
   383→                    if (tclCoroYieldPending()) {
   384→                        /* Yield occurred - stay in BODY phase for resume */
   385→                        return TCL_OK;
   386→                    }
   387→
   388→                    if (result == TCL_BREAK) {
   389→                        loop->phase = LOOP_PHASE_DONE;
   390→                        break;
   391→                    }
   392→                    if (result == TCL_CONTINUE) {
   393→                        loop->phase = LOOP_PHASE_TEST;
   394→                        break;
   395→                    }
   396→                    if (result == TCL_ERROR || result == TCL_RETURN) {
   397→                        tclCoroLoopPop();
   398→                        return result;
   399→                    }
   400→                    loop->phase = LOOP_PHASE_TEST;
   401→                    break;
   402→                }
   403→                case LOOP_PHASE_NEXT:
   404→                case LOOP_PHASE_DONE:
   405→                    break;
   406→            }
   407→        }
   408→
   409→        tclCoroLoopPop();
   410→        tclSetResult(interp, host->newString("", 0));
   411→        return TCL_OK;
   412→    }
   413→
   414→    /* No coroutine context - use simple loop (original behavior) */
   415→    tclSetResult(interp, host->newString("", 0));
   416→
   417→    while (1) {
   418→        /* Evaluate condition */
   419→        int condResult;
   420→        if (evalExprBool(interp, testStr, testLen, &condResult) != 0) {
   421→            return TCL_ERROR;
   422→        }
   423→
   424→        if (!condResult) {
   425→            break;
   426→        }
   427→
   428→        /* Execute body */
   429→        TclResult result = tclEvalObj(interp, objv[2], 0);
   430→
   431→        if (result == TCL_BREAK) {
   432→            break;
   433→        }
   434→        if (result == TCL_CONTINUE) {
   435→            continue;
   436→        }
   437→        if (result == TCL_ERROR || result == TCL_RETURN) {
   438→            return result;
   439→        }
   440→    }
   441→
   442→    tclSetResult(interp, host->newString("", 0));
   443→    return TCL_OK;
   444→}
   445→
   446→/* ========================================================================
   447→ * for Command
   448→ * ======================================================================== */
   449→
   450→TclResult tclCmdFor(TclInterp *interp, int objc, TclObj **objv) {
   451→    const TclHost *host = interp->host;
   452→
   453→    if (objc != 5) {
   454→        tclSetError(interp, "wrong # args: should be \"for start test next command\"", -1);
   455→        return TCL_ERROR;
   456→    }
   457→
   458→    /* Get test string for expr evaluation */
   459→    size_t testLen;
   460→    const char *testStr = host->getStringPtr(objv[2], &testLen);
   461→
   462→    /* Execute initialization */
   463→    TclResult result = tclEvalObj(interp, objv[1], 0);
   464→    if (result != TCL_OK) {
   465→        return result;
   466→    }
   467→
   468→    while (1) {
   469→        /* Evaluate condition */
   470→        int condResult;
   471→        if (evalExprBool(interp, testStr, testLen, &condResult) != 0) {
   472→            return TCL_ERROR;
   473→        }
   474→
   475→        if (!condResult) {
   476→            break;
   477→        }
   478→
   479→        /* Execute body */
   480→        result = tclEvalObj(interp, objv[4], 0);
   481→
   482→        /* Check for coroutine yield */
   483→        if (tclCoroYieldPending()) {
   484→            return TCL_OK;
   485→        }
   486→
   487→        if (result == TCL_BREAK) {
   488→            break;
   489→        }
   490→        if (result == TCL_CONTINUE) {
   491→            /* Fall through to next */
   492→        } else if (result == TCL_ERROR || result == TCL_RETURN) {
   493→            return result;
   494→        }
   495→
   496→        /* Execute next */
   497→        result = tclEvalObj(interp, objv[3], 0);
   498→
   499→        /* Check for coroutine yield in next */
   500→        if (tclCoroYieldPending()) {
   501→            return TCL_OK;
   502→        }
   503→
   504→        if (result != TCL_OK && result != TCL_CONTINUE) {
   505→            return result;
   506→        }
   507→    }
   508→
   509→    tclSetResult(interp, host->newString("", 0));
   510→    return TCL_OK;
   511→}
   512→
   513→/* ========================================================================
   514→ * foreach Command
   515→ * ======================================================================== */
   516→
   517→TclResult tclCmdForeach(TclInterp *interp, int objc, TclObj **objv) {
   518→    const TclHost *host = interp->host;
   519→
   520→    if (objc != 4) {
   521→        tclSetError(interp, "wrong # args: should be \"foreach varname list body\"", -1);
   522→        return TCL_ERROR;
   523→    }
   524→
   525→    size_t varNameLen;
   526→    const char *varName = host->getStringPtr(objv[1], &varNameLen);
   527→
   528→    /* Parse list */
   529→    TclObj **elems = NULL;
   530→    size_t elemCount = 0;
   531→    if (host->asList(objv[2], &elems, &elemCount) != 0) {
   532→        tclSetError(interp, "invalid list", -1);
   533→        return TCL_ERROR;
   534→    }
   535→
   536→    void *vars = interp->currentFrame->varsHandle;
   537→
   538→    for (size_t i = 0; i < elemCount; i++) {
   539→        /* Set loop variable */
   540→        host->varSet(vars, varName, varNameLen, host->dup(elems[i]));
   541→
   542→        /* Execute body */
   543→        TclResult result = tclEvalObj(interp, objv[3], 0);
   544→
   545→        /* Check for coroutine yield */
   546→        if (tclCoroYieldPending()) {
   547→            return TCL_OK;
   548→        }
   549→
   550→        if (result == TCL_BREAK) {
   551→            break;
   552→        }
   553→        if (result == TCL_CONTINUE) {
   554→            continue;
   555→        }
   556→        if (result == TCL_ERROR || result == TCL_RETURN) {
   557→            return result;
   558→        }
   559→    }
   560→
   561→    tclSetResult(interp, host->newString("", 0));
   562→    return TCL_OK;
   563→}
