     1→/*
     2→ * eval.c - TCL Evaluator
     3→ *
     4→ * Evaluates scripts by parsing to AST and walking the tree structure.
     5→ * Uses an explicit state stack for non-recursive evaluation,
     6→ * enabling coroutine suspend/resume by saving the stack state.
     7→ */
     8→
     9→#include "internal.h"
    10→#include "ast.h"
    11→
    12→/* ========================================================================
    13→ * Interpreter Creation and Destruction
    14→ * ======================================================================== */
    15→
    16→TclInterp *tclInterpNew(const TclHost *host, void *hostCtx) {
    17→    /* Allocate interpreter struct using host arena temporarily */
    18→    void *arena = host->arenaPush(hostCtx);
    19→    TclInterp *interp = host->arenaAlloc(arena, sizeof(TclInterp), sizeof(void*));
    20→    if (!interp) {
    21→        host->arenaPop(hostCtx, arena);
    22→        return NULL;
    23→    }
    24→
    25→    /* Copy to permanent storage (we need interp to outlive arena) */
    26→    /* For now, use a simple static pool approach */
    27→    static TclInterp interpPool[8];
    28→    static int interpCount = 0;
    29→    if (interpCount >= 8) {
    30→        host->arenaPop(hostCtx, arena);
    31→        return NULL;
    32→    }
    33→
    34→    host->arenaPop(hostCtx, arena);
    35→    interp = &interpPool[interpCount++];
    36→
    37→    /* Initialize */
    38→    interp->host = host;
    39→    interp->hostCtx = hostCtx;
    40→    interp->result = NULL;
    41→    interp->resultCode = TCL_OK;
    42→    interp->errorInfo = NULL;
    43→    interp->errorCode = NULL;
    44→    interp->errorLine = 0;
    45→    interp->returnCode = TCL_OK;
    46→    interp->returnLevel = 1;
    47→    interp->currentCoro = NULL;
    48→    interp->scriptFile = NULL;
    49→    interp->scriptLine = 0;
    50→    interp->parent = NULL;
    51→    interp->isSafe = 0;
    52→
    53→    /* Create global frame */
    54→    interp->globalFrame = host->frameAlloc(hostCtx);
    55→    if (!interp->globalFrame) {
    56→        return NULL;
    57→    }
    58→    interp->globalFrame->parent = NULL;
    59→    interp->globalFrame->level = 0;
    60→    interp->globalFrame->flags = TCL_FRAME_GLOBAL;
    61→    interp->globalFrame->procName = NULL;
    62→    interp->globalFrame->invocationObjs = NULL;
    63→    interp->globalFrame->invocationCount = 0;
    64→
    65→    interp->currentFrame = interp->globalFrame;
    66→
    67→    return interp;
    68→}
    69→
    70→void tclInterpFree(TclInterp *interp) {
    71→    if (!interp) return;
    72→
    73→    const TclHost *host = interp->host;
    74→    void *ctx = interp->hostCtx;
    75→
    76→    /* Free global frame */
    77→    if (interp->globalFrame) {
    78→        host->frameFree(ctx, interp->globalFrame);
    79→    }
    80→
    81→    /* Note: interp itself is from static pool, not freed */
    82→}
    83→
    84→/* ========================================================================
    85→ * Result and Error Handling
    86→ * ======================================================================== */
    87→
    88→TclObj *tclGetResult(TclInterp *interp) {
    89→    return interp->result;
    90→}
    91→
    92→void tclSetResult(TclInterp *interp, TclObj *result) {
    93→    interp->result = result;
    94→    interp->resultCode = TCL_OK;
    95→}
    96→
    97→void tclSetResultStr(TclInterp *interp, const char *s, size_t len) {
    98→    if (len == (size_t)-1) {
    99→        len = tclStrlen(s);
   100→    }
   101→    interp->result = interp->host->newString(s, len);
   102→    interp->resultCode = TCL_OK;
   103→}
   104→
   105→void tclSetError(TclInterp *interp, const char *msg, size_t len) {
   106→    if (len == (size_t)-1) {
   107→        len = tclStrlen(msg);
   108→    }
   109→    interp->result = interp->host->newString(msg, len);
   110→    interp->resultCode = TCL_ERROR;
   111→}
   112→
   113→void tclSetErrorCode(TclInterp *interp, TclObj *code) {
   114→    interp->errorCode = code;
   115→}
   116→
   117→void tclAddErrorInfo(TclInterp *interp, const char *info, size_t len) {
   118→    if (len == (size_t)-1) {
   119→        len = tclStrlen(info);
   120→    }
   121→
   122→    const TclHost *host = interp->host;
   123→
   124→    if (!interp->errorInfo) {
   125→        interp->errorInfo = host->newString(info, len);
   126→    } else {
   127→        /* Append to existing error info */
   128→        size_t existingLen;
   129→        const char *existing = host->getStringPtr(interp->errorInfo, &existingLen);
   130→
   131→        void *arena = host->arenaPush(interp->hostCtx);
   132→        char *buf = host->arenaAlloc(arena, existingLen + len + 1, 1);
   133→
   134→        for (size_t i = 0; i < existingLen; i++) {
   135→            buf[i] = existing[i];
   136→        }
   137→        for (size_t i = 0; i < len; i++) {
   138→            buf[existingLen + i] = info[i];
   139→        }
   140→        buf[existingLen + len] = '\0';
   141→
   142→        interp->errorInfo = host->newString(buf, existingLen + len);
   143→        host->arenaPop(interp->hostCtx, arena);
   144→    }
   145→}
   146→
   147→/* ========================================================================
   148→ * Evaluation State
   149→ *
   150→ * The evaluator maintains an explicit stack of states instead of using
   151→ * C recursion. This allows the evaluation to be suspended (for yield)
   152→ * and resumed later by saving/restoring the state stack.
   153→ * ======================================================================== */
   154→
   155→typedef enum {
   156→    EVAL_PHASE_SCRIPT,      /* Evaluating a script node */
   157→    EVAL_PHASE_COMMAND,     /* Evaluating a command node */
   158→    EVAL_PHASE_WORD,        /* Evaluating a word node */
   159→    EVAL_PHASE_VAR,         /* Looking up a variable */
   160→    EVAL_PHASE_CMD_SUBST,   /* Evaluating command substitution */
   161→} EvalPhase;
   162→
   163→typedef struct EvalFrame {
   164→    EvalPhase phase;
   165→    TclAstNode *node;           /* Current node being evaluated */
   166→    int index;                  /* Current child index */
   167→    TclObj **args;              /* Accumulated arguments (for commands) */
   168→    int argCount;               /* Number of accumulated arguments */
   169→    int argCapacity;            /* Capacity of args array */
   170→    TclObj *result;             /* Partial result for words */
   171→    struct EvalFrame *parent;
   172→} EvalFrame;
   173→
   174→/* Maximum stack depth */
   175→enum { MAX_EVAL_DEPTH = 256 };
   176→
   177→typedef struct EvalState {
   178→    EvalFrame *top;             /* Top of evaluation stack */
   179→    void *arena;                /* Arena for allocation */
   180→    int suspended;              /* Was evaluation suspended (yield)? */
   181→    TclObj *yieldValue;         /* Value passed to yield */
   182→} EvalState;
   183→
   184→/* ========================================================================
   185→ * State Management
   186→ * ======================================================================== */
   187→
   188→static EvalFrame *pushFrame(TclInterp *interp, EvalState *state, EvalPhase phase, TclAstNode *node) {
   189→    const TclHost *host = interp->host;
   190→
   191→    EvalFrame *frame = host->arenaAlloc(state->arena, sizeof(EvalFrame), sizeof(void*));
   192→    if (!frame) return NULL;
   193→
   194→    frame->phase = phase;
   195→    frame->node = node;
   196→    frame->index = 0;
   197→    frame->args = NULL;
   198→    frame->argCount = 0;
   199→    frame->argCapacity = 0;
   200→    frame->result = NULL;
   201→    frame->parent = state->top;
   202→
   203→    state->top = frame;
   204→    return frame;
   205→}
   206→
   207→static void popFrame(EvalState *state) {
   208→    if (state->top) {
   209→        state->top = state->top->parent;
   210→    }
   211→}
   212→
   213→/* Add argument to command frame */
   214→static int addArg(TclInterp *interp, EvalState *state, EvalFrame *frame, TclObj *arg) {
   215→    const TclHost *host = interp->host;
   216→
   217→    if (frame->argCount >= frame->argCapacity) {
   218→        int newCap = frame->argCapacity == 0 ? 8 : frame->argCapacity * 2;
   219→        TclObj **newArgs = host->arenaAlloc(state->arena, newCap * sizeof(TclObj*), sizeof(void*));
   220→        if (!newArgs) return -1;
   221→
   222→        for (int i = 0; i < frame->argCount; i++) {
   223→            newArgs[i] = frame->args[i];
   224→        }
   225→        frame->args = newArgs;
   226→        frame->argCapacity = newCap;
   227→    }
   228→
   229→    frame->args[frame->argCount++] = arg;
   230→    return 0;
   231→}
   232→
   233→/* Concatenate result for word building */
   234→static int appendResult(TclInterp *interp, EvalState *state, EvalFrame *frame, TclObj *part) {
   235→    const TclHost *host = interp->host;
   236→
   237→    if (!frame->result) {
   238→        frame->result = part;
   239→        return 0;
   240→    }
   241→
   242→    /* Concatenate strings */
   243→    size_t len1, len2;
   244→    const char *s1 = host->getStringPtr(frame->result, &len1);
   245→    const char *s2 = host->getStringPtr(part, &len2);
   246→
   247→    char *buf = host->arenaAlloc(state->arena, len1 + len2 + 1, 1);
   248→    if (!buf) return -1;
   249→
   250→    for (size_t i = 0; i < len1; i++) buf[i] = s1[i];
   251→    for (size_t i = 0; i < len2; i++) buf[len1 + i] = s2[i];
   252→    buf[len1 + len2] = '\0';
   253→
   254→    frame->result = host->newString(buf, len1 + len2);
   255→    return 0;
   256→}
   257→
   258→/* ========================================================================
   259→ * Variable Lookup
   260→ * ======================================================================== */
   261→
   262→static TclObj *lookupVar(TclInterp *interp, const char *name, size_t len) {
   263→    const TclHost *host = interp->host;
   264→
   265→    /* Try current frame first */
   266→    TclObj *val = host->varGet(interp->currentFrame->varsHandle, name, len);
   267→    if (val) return val;
   268→
   269→    /* Try global frame if different */
   270→    if (interp->currentFrame != interp->globalFrame) {
   271→        val = host->varGet(interp->globalFrame->varsHandle, name, len);
   272→    }
   273→
   274→    return val;
   275→}
   276→
   277→/* ========================================================================
   278→ * Command Dispatch
   279→ * ======================================================================== */
   280→
   281→static TclResult dispatchCommand(TclInterp *interp, int objc, TclObj **objv) {
   282→    const TclHost *host = interp->host;
   283→
   284→    if (objc == 0) {
   285→        tclSetResult(interp, host->newString("", 0));
   286→        return TCL_OK;
   287→    }
   288→
   289→    size_t cmdLen;
   290→    const char *cmdName = host->getStringPtr(objv[0], &cmdLen);
   291→
   292→    /* Check builtins first */
   293→    int builtinIdx = tclBuiltinLookup(cmdName, cmdLen);
   294→    if (builtinIdx >= 0) {
   295→        const TclBuiltinEntry *entry = tclBuiltinGet(builtinIdx);
   296→        if (entry) {
   297→            return entry->proc(interp, objc, objv);
   298→        }
   299→    }
   300→
   301→    /* Try host command lookup (procs, extensions) */
   302→    TclCmdInfo cmdInfo;
   303→    if (host->cmdLookup(interp->hostCtx, cmdName, cmdLen, &cmdInfo) == 0) {
   304→        switch (cmdInfo.type) {
   305→            case TCL_CMD_PROC: {
   306→                TclObj *argList = NULL;
   307→                TclObj *body = NULL;
   308→                if (host->procGetDef(cmdInfo.u.procHandle, &argList, &body) != 0) {
   309→                    tclSetError(interp, "proc definition not found", -1);
   310→                    return TCL_ERROR;
   311→                }
   312→
   313→                /* Parse argument specification */
   314→                TclObj **argSpecs = NULL;
   315→                size_t argCount = 0;
   316→                if (host->asList(argList, &argSpecs, &argCount) != 0) {
   317→                    tclSetError(interp, "invalid argument list", -1);
   318→                    return TCL_ERROR;
   319→                }
   320→
   321→                /* Create proc frame */
   322→                TclFrame *procFrame = host->frameAlloc(interp->hostCtx);
   323→                if (!procFrame) {
   324→                    tclSetError(interp, "out of memory", -1);
   325→                    return TCL_ERROR;
   326→                }
   327→                procFrame->parent = interp->currentFrame;
   328→                procFrame->level = interp->currentFrame->level + 1;
   329→                procFrame->flags = TCL_FRAME_PROC;
   330→                procFrame->procName = cmdName;
   331→                procFrame->invocationObjs = objv;
   332→                procFrame->invocationCount = objc;
   333→
   334→                /* Check for 'args' parameter */
   335→                int hasArgs = 0;
   336→                if (argCount > 0) {
   337→                    size_t lastLen;
   338→                    const char *lastName = host->getStringPtr(argSpecs[argCount - 1], &lastLen);
   339→                    if (lastLen == 4 && tclStrncmp(lastName, "args", 4) == 0) {
   340→                        hasArgs = 1;
   341→                    }
   342→                }
   343→
   344→                int requiredArgs = (int)argCount - (hasArgs ? 1 : 0);
   345→                int actualArgs = objc - 1;
   346→
   347→                /* Count args with defaults */
   348→                int minArgs = 0;
   349→                for (size_t i = 0; i < (size_t)requiredArgs; i++) {
   350→                    if (host->listLength(argSpecs[i]) < 2) {
   351→                        minArgs++;
   352→                    }
   353→                }
   354→
   355→                if (actualArgs < minArgs || (!hasArgs && actualArgs > requiredArgs)) {
   356→                    tclSetError(interp, "wrong # args", -1);
   357→                    host->frameFree(interp->hostCtx, procFrame);
   358→                    return TCL_ERROR;
   359→                }
   360→
   361→                /* Bind arguments */
   362→                for (size_t i = 0; i < (size_t)requiredArgs; i++) {
   363→                    TclObj *argSpec = argSpecs[i];
   364→                    size_t argNameLen;
   365→                    const char *argName;
   366→                    TclObj *value = NULL;
   367→
   368→                    size_t listLen = host->listLength(argSpec);
   369→                    if (listLen >= 2) {
   370→                        TclObj *nameObj = host->listIndex(argSpec, 0);
   371→                        argName = host->getStringPtr(nameObj, &argNameLen);
   372→                        if ((int)i < actualArgs) {
   373→                            value = objv[i + 1];
   374→                        } else {
   375→                            value = host->listIndex(argSpec, 1);
   376→                        }
   377→                    } else {
   378→                        argName = host->getStringPtr(argSpec, &argNameLen);
   379→                        if ((int)i < actualArgs) {
   380→                            value = objv[i + 1];
   381→                        }
   382→                    }
   383→
   384→                    if (value) {
   385→                        host->varSet(procFrame->varsHandle, argName, argNameLen, host->dup(value));
   386→                    }
   387→                }
   388→
   389→                if (hasArgs) {
   390→                    int argsStart = requiredArgs;
   391→                    int argsCount = actualArgs - argsStart;
   392→                    if (argsCount < 0) argsCount = 0;
   393→
   394→                    TclObj *argsList;
   395→                    if (argsCount > 0) {
   396→                        argsList = host->newList(&objv[argsStart + 1], argsCount);
   397→                    } else {
   398→                        argsList = host->newString("", 0);
   399→                    }
   400→                    host->varSet(procFrame->varsHandle, "args", 4, argsList);
   401→                }
   402→
   403→                /* Execute body */
   404→                TclFrame *savedFrame = interp->currentFrame;
   405→                interp->currentFrame = procFrame;
   406→
   407→                TclResult result = tclEvalObj(interp, body, 0);
   408→
   409→                if (result == TCL_RETURN) {
   410→                    result = TCL_OK;
   411→                }
   412→
   413→                interp->currentFrame = savedFrame;
   414→                host->frameFree(interp->hostCtx, procFrame);
   415→                return result;
   416→            }
   417→
   418→            case TCL_CMD_EXTENSION:
   419→                return host->extInvoke(interp, cmdInfo.u.extHandle, objc, objv);
   420→
   421→            default:
   422→                break;
   423→        }
   424→    }
   425→
   426→    /* Check coroutines */
   427→    TclCoroutine *coro = tclCoroLookup(cmdName, cmdLen);
   428→    if (coro) {
   429→        return tclCoroInvoke(interp, coro, objc, objv);
   430→    }
   431→
   432→    /* Command not found */
   433→    void *arena = host->arenaPush(interp->hostCtx);
   434→    char *msg = host->arenaAlloc(arena, cmdLen + 30, 1);
   435→    char *p = msg;
   436→    const char *prefix = "invalid command name \"";
   437→    while (*prefix) *p++ = *prefix++;
   438→    for (size_t i = 0; i < cmdLen; i++) *p++ = cmdName[i];
   439→    *p++ = '"';
   440→    *p = '\0';
   441→    tclSetError(interp, msg, p - msg);
   442→    host->arenaPop(interp->hostCtx, arena);
   443→    return TCL_ERROR;
   444→}
   445→
   446→/* ========================================================================
   447→ * Evaluation Step
   448→ *
   449→ * Processes one step of evaluation. Returns:
   450→ * - TCL_OK: Evaluation complete
   451→ * - TCL_ERROR: Error occurred
   452→ * - TCL_CONTINUE: More work to do (internal use)
   453→ * ======================================================================== */
   454→
   455→static TclResult evalStep(TclInterp *interp, EvalState *state) {
   456→    const TclHost *host = interp->host;
   457→
   458→    if (!state->top) {
   459→        return TCL_OK; /* Stack empty, done */
   460→    }
   461→
   462→    EvalFrame *frame = state->top;
   463→
   464→    switch (frame->phase) {
   465→        case EVAL_PHASE_SCRIPT: {
   466→            TclAstNode *script = frame->node;
   467→
   468→            if (frame->index >= script->u.script.count) {
   469→                /* Script done */
   470→                popFrame(state);
   471→                return TCL_CONTINUE;
   472→            }
   473→
   474→            /* Push frame for next command */
   475→            TclAstNode *cmd = script->u.script.cmds[frame->index++];
   476→            if (!pushFrame(interp, state, EVAL_PHASE_COMMAND, cmd)) {
   477→                tclSetError(interp, "out of memory", -1);
   478→                return TCL_ERROR;
   479→            }
   480→            return TCL_CONTINUE;
   481→        }
   482→
   483→        case EVAL_PHASE_COMMAND: {
   484→            TclAstNode *cmd = frame->node;
   485→
   486→            if (frame->index >= cmd->u.command.count) {
   487→                /* All words evaluated, dispatch command */
   488→                TclResult result = dispatchCommand(interp, frame->argCount, frame->args);
   489→
   490→                /* Handle control flow */
   491→                if (result == TCL_ERROR || result == TCL_RETURN ||
   492→                    result == TCL_BREAK || result == TCL_CONTINUE) {
   493→                    return result;
   494→                }
   495→
   496→                /* Check for yield */
   497→                if (tclCoroYieldPending()) {
   498→                    state->suspended = 1;
   499→                    return TCL_OK;
   500→                }
   501→
   502→                popFrame(state);
   503→                return TCL_CONTINUE;
   504→            }
   505→
   506→            /* Push frame for next word */
   507→            TclAstNode *wordNode = cmd->u.command.words[frame->index++];
   508→
   509→            /* Handle expand nodes */
   510→            if (wordNode->type == TCL_NODE_EXPAND) {
   511→                /* Push word evaluation for inner node */
   512→                if (!pushFrame(interp, state, EVAL_PHASE_WORD, wordNode->u.expand.word)) {
   513→                    tclSetError(interp, "out of memory", -1);
   514→                    return TCL_ERROR;
   515→                }
   516→                /* Mark that we need to expand the result */
   517→                state->top->phase = EVAL_PHASE_WORD;
   518→                /* TODO: Handle expansion after word evaluation */
   519→            } else if (wordNode->type == TCL_NODE_LITERAL) {
   520→                /* Literal - just add as argument */
   521→                TclObj *arg = host->newString(wordNode->u.literal.value, wordNode->u.literal.len);
   522→                if (addArg(interp, state, frame, arg) != 0) {
   523→                    tclSetError(interp, "out of memory", -1);
   524→                    return TCL_ERROR;
   525→                }
   526→            } else if (wordNode->type == TCL_NODE_VAR_SIMPLE) {
   527→                /* Variable reference */
   528→                TclObj *val = lookupVar(interp, wordNode->u.varSimple.name, wordNode->u.varSimple.len);
   529→                if (!val) {
   530→                    void *arena = host->arenaPush(interp->hostCtx);
   531→                    char *msg = host->arenaAlloc(arena, wordNode->u.varSimple.len + 30, 1);
   532→                    char *p = msg;
   533→                    const char *prefix = "can't read \"";
   534→                    while (*prefix) *p++ = *prefix++;
   535→                    for (size_t i = 0; i < wordNode->u.varSimple.len; i++) *p++ = wordNode->u.varSimple.name[i];
   536→                    const char *suffix = "\": no such variable";
   537→                    while (*suffix) *p++ = *suffix++;
   538→                    *p = '\0';
   539→                    tclSetError(interp, msg, p - msg);
   540→                    host->arenaPop(interp->hostCtx, arena);
   541→                    return TCL_ERROR;
   542→                }
   543→                if (addArg(interp, state, frame, val) != 0) {
   544→                    tclSetError(interp, "out of memory", -1);
   545→                    return TCL_ERROR;
   546→                }
   547→            } else {
   548→                /* Complex word - push word frame */
   549→                if (!pushFrame(interp, state, EVAL_PHASE_WORD, wordNode)) {
   550→                    tclSetError(interp, "out of memory", -1);
   551→                    return TCL_ERROR;
   552→                }
   553→            }
   554→            return TCL_CONTINUE;
   555→        }
   556→
   557→        case EVAL_PHASE_WORD: {
   558→            TclAstNode *word = frame->node;
   559→
   560→            if (word->type == TCL_NODE_LITERAL) {
   561→                /* Simple literal */
   562→                frame->result = host->newString(word->u.literal.value, word->u.literal.len);
   563→                goto word_done;
   564→            }
   565→
   566→            if (word->type == TCL_NODE_BACKSLASH) {
   567→                /* Backslash escape */
   568→                frame->result = host->newString(word->u.backslash.value, word->u.backslash.len);
   569→                goto word_done;
   570→            }
   571→
   572→            if (word->type == TCL_NODE_VAR_SIMPLE) {
   573→                TclObj *val = lookupVar(interp, word->u.varSimple.name, word->u.varSimple.len);
   574→                if (!val) {
   575→                    void *arena = host->arenaPush(interp->hostCtx);
   576→                    char *msg = host->arenaAlloc(arena, word->u.varSimple.len + 30, 1);
   577→                    char *p = msg;
   578→                    const char *prefix = "can't read \"";
   579→                    while (*prefix) *p++ = *prefix++;
   580→                    for (size_t i = 0; i < word->u.varSimple.len; i++) *p++ = word->u.varSimple.name[i];
   581→                    const char *suffix = "\": no such variable";
   582→                    while (*suffix) *p++ = *suffix++;
   583→                    *p = '\0';
   584→                    tclSetError(interp, msg, p - msg);
   585→                    host->arenaPop(interp->hostCtx, arena);
   586→                    return TCL_ERROR;
   587→                }
   588→                frame->result = val;
   589→                goto word_done;
   590→            }
   591→
   592→            if (word->type == TCL_NODE_VAR_ARRAY) {
   593→                /* Array reference - first evaluate index */
   594→                if (frame->index == 0) {
   595→                    frame->index = 1;
   596→                    if (word->u.varArray.index) {
   597→                        if (!pushFrame(interp, state, EVAL_PHASE_WORD, word->u.varArray.index)) {
   598→                            tclSetError(interp, "out of memory", -1);
   599→                            return TCL_ERROR;
   600→                        }
   601→                        return TCL_CONTINUE;
   602→                    }
   603→                }
   604→
   605→                /* Index evaluated, look up array element */
   606→                const char *arrName = word->u.varArray.name;
   607→                size_t arrLen = word->u.varArray.nameLen;
   608→                size_t indexLen;
   609→                const char *indexStr = "";
   610→                if (frame->result) {
   611→                    indexStr = host->getStringPtr(frame->result, &indexLen);
   612→                } else {
   613→                    indexLen = 0;
   614→                }
   615→
   616→                /* Build array(index) key */
   617→                char *key = host->arenaAlloc(state->arena, arrLen + indexLen + 3, 1);
   618→                char *p = key;
   619→                for (size_t i = 0; i < arrLen; i++) *p++ = arrName[i];
   620→                *p++ = '(';
   621→                for (size_t i = 0; i < indexLen; i++) *p++ = indexStr[i];
   622→                *p++ = ')';
   623→                *p = '\0';
   624→
   625→                TclObj *val = lookupVar(interp, key, p - key);
   626→                if (!val) {
   627→                    void *arena = host->arenaPush(interp->hostCtx);
   628→                    char *msg = host->arenaAlloc(arena, (p - key) + 30, 1);
   629→                    char *mp = msg;
   630→                    const char *prefix = "can't read \"";
   631→                    while (*prefix) *mp++ = *prefix++;
   632→                    for (char *k = key; k < p; k++) *mp++ = *k;
   633→                    const char *suffix = "\": no such variable";
   634→                    while (*suffix) *mp++ = *suffix++;
   635→                    *mp = '\0';
   636→                    tclSetError(interp, msg, mp - msg);
   637→                    host->arenaPop(interp->hostCtx, arena);
   638→                    return TCL_ERROR;
   639→                }
   640→                frame->result = val;
   641→                goto word_done;
   642→            }
   643→
   644→            if (word->type == TCL_NODE_CMD_SUBST) {
   645→                /* Command substitution */
   646→                if (frame->index == 0) {
   647→                    frame->index = 1;
   648→                    if (word->u.cmdSubst.script) {
   649→                        if (!pushFrame(interp, state, EVAL_PHASE_SCRIPT, word->u.cmdSubst.script)) {
   650→                            tclSetError(interp, "out of memory", -1);
   651→                            return TCL_ERROR;
   652→                        }
   653→                        return TCL_CONTINUE;
   654→                    }
   655→                }
   656→                /* Script evaluated, result is in interp->result */
   657→                frame->result = interp->result ? interp->result : host->newString("", 0);
   658→                goto word_done;
   659→            }
   660→
   661→            if (word->type == TCL_NODE_WORD) {
   662→                /* Composite word - evaluate parts */
   663→                if (frame->index >= word->u.word.count) {
   664→                    /* All parts done */
   665→                    if (!frame->result) {
   666→                        frame->result = host->newString("", 0);
   667→                    }
   668→                    goto word_done;
   669→                }
   670→
   671→                TclAstNode *part = word->u.word.parts[frame->index++];
   672→
   673→                /* Handle simple parts inline */
   674→                if (part->type == TCL_NODE_LITERAL) {
   675→                    TclObj *lit = host->newString(part->u.literal.value, part->u.literal.len);
   676→                    if (appendResult(interp, state, frame, lit) != 0) {
   677→                        tclSetError(interp, "out of memory", -1);
   678→                        return TCL_ERROR;
   679→                    }
   680→                    return TCL_CONTINUE;
   681→                }
   682→
   683→                if (part->type == TCL_NODE_BACKSLASH) {
   684→                    TclObj *bs = host->newString(part->u.backslash.value, part->u.backslash.len);
   685→                    if (appendResult(interp, state, frame, bs) != 0) {
   686→                        tclSetError(interp, "out of memory", -1);
   687→                        return TCL_ERROR;
   688→                    }
   689→                    return TCL_CONTINUE;
   690→                }
   691→
   692→                /* Complex part - push frame */
   693→                if (!pushFrame(interp, state, EVAL_PHASE_WORD, part)) {
   694→                    tclSetError(interp, "out of memory", -1);
   695→                    return TCL_ERROR;
   696→                }
   697→                return TCL_CONTINUE;
   698→            }
   699→
   700→            /* Unknown node type */
   701→            tclSetError(interp, "internal error: unknown node type", -1);
   702→            return TCL_ERROR;
   703→
   704→        word_done:
   705→            /* Word evaluation complete */
   706→            popFrame(state);
   707→
   708→            /* If parent is a command, add as argument */
   709→            if (state->top && state->top->phase == EVAL_PHASE_COMMAND) {
   710→                if (addArg(interp, state, state->top, frame->result) != 0) {
   711→                    tclSetError(interp, "out of memory", -1);
   712→                    return TCL_ERROR;
   713→                }
   714→            }
   715→            /* If parent is a word, append to result */
   716→            else if (state->top && state->top->phase == EVAL_PHASE_WORD) {
   717→                if (appendResult(interp, state, state->top, frame->result) != 0) {
   718→                    tclSetError(interp, "out of memory", -1);
   719→                    return TCL_ERROR;
   720→                }
   721→            }
   722→            return TCL_CONTINUE;
   723→        }
   724→
   725→        default:
   726→            tclSetError(interp, "internal error: invalid eval phase", -1);
   727→            return TCL_ERROR;
   728→    }
   729→}
   730→
   731→/* ========================================================================
   732→ * High-Level Eval Functions
   733→ * ======================================================================== */
   734→
   735→static TclResult evalAst(TclInterp *interp, TclAstNode *ast) {
   736→    const TclHost *host = interp->host;
   737→
   738→    if (!ast) {
   739→        tclSetResult(interp, host->newString("", 0));
   740→        return TCL_OK;
   741→    }
   742→
   743→    /* Initialize state */
   744→    void *arena = host->arenaPush(interp->hostCtx);
   745→    EvalState state;
   746→    state.top = NULL;
   747→    state.arena = arena;
   748→    state.suspended = 0;
   749→    state.yieldValue = NULL;
   750→
   751→    /* Push initial frame */
   752→    if (ast->type == TCL_NODE_SCRIPT) {
   753→        if (!pushFrame(interp, &state, EVAL_PHASE_SCRIPT, ast)) {
   754→            host->arenaPop(interp->hostCtx, arena);
   755→            tclSetError(interp, "out of memory", -1);
   756→            return TCL_ERROR;
   757→        }
   758→    } else {
   759→        /* Single node - wrap in evaluation */
   760→        if (!pushFrame(interp, &state, EVAL_PHASE_WORD, ast)) {
   761→            host->arenaPop(interp->hostCtx, arena);
   762→            tclSetError(interp, "out of memory", -1);
   763→            return TCL_ERROR;
   764→        }
   765→    }
   766→
   767→    /* Set default result */
   768→    tclSetResult(interp, host->newString("", 0));
   769→
   770→    /* Run evaluation loop */
   771→    TclResult result;
   772→    while (1) {
   773→        result = evalStep(interp, &state);
   774→
   775→        if (result == TCL_CONTINUE) {
   776→            continue;
   777→        }
   778→
   779→        if (state.suspended) {
   780→            /* Yield occurred - need to save state for resume */
   781→            /* For now, just return */
   782→            break;
   783→        }
   784→
   785→        break;
   786→    }
   787→
   788→    host->arenaPop(interp->hostCtx, arena);
   789→    return result == TCL_CONTINUE ? TCL_OK : result;
   790→}
   791→
   792→TclResult tclEvalScript(TclInterp *interp, const char *script, size_t len, int flags) {
   793→    const TclHost *host = interp->host;
   794→
   795→    if (len == 0 || !script) {
   796→        tclSetResult(interp, host->newString("", 0));
   797→        return TCL_OK;
   798→    }
   799→
   800→    /* Handle TCL_EVAL_GLOBAL flag */
   801→    TclFrame *savedFrame = NULL;
   802→    if (flags & TCL_EVAL_GLOBAL) {
   803→        savedFrame = interp->currentFrame;
   804→        interp->currentFrame = interp->globalFrame;
   805→    }
   806→
   807→    /* Parse script to AST */
   808→    void *arena = host->arenaPush(interp->hostCtx);
   809→    TclAstNode *ast = tclAstParse(interp, arena, script, len);
   810→
   811→    if (!ast) {
   812→        host->arenaPop(interp->hostCtx, arena);
   813→        if (savedFrame) {
   814→            interp->currentFrame = savedFrame;
   815→        }
   816→        tclSetError(interp, "parse error", -1);
   817→        return TCL_ERROR;
   818→    }
   819→
   820→    /* Evaluate AST */
   821→    TclResult result = evalAst(interp, ast);
   822→
   823→    host->arenaPop(interp->hostCtx, arena);
   824→
   825→    /* Restore frame if we switched to global */
   826→    if (savedFrame) {
   827→        interp->currentFrame = savedFrame;
   828→    }
   829→
   830→    return result;
   831→}
   832→
   833→TclResult tclEvalObjv(TclInterp *interp, int objc, TclObj **objv, int flags) {
   834→    /* Handle TCL_EVAL_GLOBAL flag */
   835→    TclFrame *savedFrame = NULL;
   836→    if (flags & TCL_EVAL_GLOBAL) {
   837→        savedFrame = interp->currentFrame;
   838→        interp->currentFrame = interp->globalFrame;
   839→    }
   840→
   841→    TclResult result = dispatchCommand(interp, objc, objv);
   842→
   843→    /* Restore frame if we switched to global */
   844→    if (savedFrame) {
   845→        interp->currentFrame = savedFrame;
   846→    }
   847→
   848→    return result;
   849→}
   850→
   851→TclResult tclEvalBracketed(TclInterp *interp, const char *cmd, size_t len) {
   852→    /* Save current result */
   853→    TclObj *savedResult = interp->result;
   854→    TclResult savedCode = interp->resultCode;
   855→
   856→    /* Evaluate bracketed command in current scope */
   857→    TclResult result = tclEvalScript(interp, cmd, len, 0);
   858→
   859→    if (result != TCL_OK) {
   860→        /* Keep error result */
   861→        return result;
   862→    }
   863→
   864→    /* Command succeeded - result is in interp->result */
   865→    /* Restore saved code but keep new result */
   866→    (void)savedResult;
   867→    (void)savedCode;
   868→
   869→    return TCL_OK;
   870→}
   871→
   872→/* ========================================================================
   873→ * Public API (from tclc.h)
   874→ * ======================================================================== */
   875→
   876→TclResult tclEvalObj(TclInterp *interp, TclObj *script, int flags) {
   877→    size_t len;
   878→    const char *str = interp->host->getStringPtr(script, &len);
   879→    return tclEvalScript(interp, str, len, flags);
   880→}
