   260→  TclObj *(*dup)(TclObj *obj);
   261→
   262→  /* String representation (always available) */
   263→  const char *(*getStringPtr)(TclObj *obj, size_t *lenOut);
   264→
   265→  /* Type conversions (may fail, return 0 on success) */
   266→  int (*asInt)(TclObj *obj, int64_t *out);
   267→  int (*asDouble)(TclObj *obj, double *out);
   268→  int (*asBool)(TclObj *obj, int *out);
   269→  int (*asList)(TclObj *obj, TclObj ***elemsOut, size_t *countOut);
   270→
   271→  /* ==================================================================
   272→   * List Operations
   273→   * ================================================================== */
   274→
   275→  size_t (*listLength)(TclObj *list);
   276→  TclObj *(*listIndex)(TclObj *list, size_t idx);
   277→  TclObj *(*listRange)(TclObj *list, size_t first, size_t last);
   278→  TclObj *(*listSet)(TclObj *list, size_t idx, TclObj *val);
   279→  TclObj *(*listAppend)(TclObj *list, TclObj *elem);
