   287→   * ================================================================== */
   288→
   289→  TclObj *(*dictGet)(TclObj *dict, TclObj *key);
   290→  TclObj *(*dictSet)(TclObj *dict, TclObj *key, TclObj *val);
   291→  int (*dictExists)(TclObj *dict, TclObj *key);
   292→  TclObj *(*dictKeys)(TclObj *dict, const char *pattern);
   293→  TclObj *(*dictValues)(TclObj *dict, const char *pattern);
   294→  TclObj *(*dictRemove)(TclObj *dict, TclObj *key);
   295→  size_t (*dictSize)(TclObj *dict);
   296→
   297→  /* ==================================================================
   298→   * String Operations
   299→   *
   300→   * String length/index are in characters (Unicode code points),
   301→   * not bytes. Host handles UTF-8 encoding.
   302→   * ================================================================== */
   303→
   304→  size_t (*stringLength)(TclObj *str);
   305→  TclObj *(*stringIndex)(TclObj *str, size_t idx);
   306→  TclObj *(*stringRange)(TclObj *str, size_t first, size_t last);
   307→  TclObj *(*stringConcat)(TclObj **parts, size_t count);
   308→  int (*stringCompare)(TclObj *a, TclObj *b);
   309→  int (*stringCompareNocase)(TclObj *a, TclObj *b);
   310→  int (*stringMatch)(const char *pattern, TclObj *str, int nocase);
   311→  TclObj *(*stringToLower)(TclObj *str);
