   331→
   332→  /* ==================================================================
   333→   * Variables (Scalars)
   334→   *
   335→   * Each frame has a varsHandle for local variables.
   336→   * The host special-cases "env" array to route to environment.
   337→   * ================================================================== */
   338→
   339→  void *(*varsNew)(void *ctx);
   340→  void (*varsFree)(void *ctx, void *vars);
   341→  TclObj *(*varGet)(void *vars, const char *name, size_t len);
   342→  void (*varSet)(void *vars, const char *name, size_t len, TclObj *val);
   343→  void (*varUnset)(void *vars, const char *name, size_t len);
   344→  int (*varExists)(void *vars, const char *name, size_t len);
   345→  TclObj *(*varNames)(void *vars, const char *pattern);
   346→  TclObj *(*varNamesLocal)(void *vars,
   347→                           const char *pattern); /* Excludes linked vars */
   348→  void (*varLink)(void *localVars, const char *localName, size_t localLen,
   349→                  void *targetVars, const char *targetName, size_t targetLen);
   350→
   351→  /* ==================================================================
   352→   * Arrays
   353→   *
   354→   * TCL arrays are associative: $arr(key) syntax.
   355→   * Distinct from lists and dicts.
   356→   * ================================================================== */
   357→
   358→  void (*arraySet)(void *vars, const char *arr, size_t arrLen, const char *key,
   359→                   size_t keyLen, TclObj *val);
   360→  TclObj *(*arrayGet)(void *vars, const char *arr, size_t arrLen,
   361→                      const char *key, size_t keyLen);
   362→  int (*arrayExists)(void *vars, const char *arr, size_t arrLen,
