   369→
   370→  /* Array search/iteration */
   371→  TclObj *(*arrayStartSearch)(void *vars, const char *arr, size_t arrLen);
   372→  int (*arrayAnymore)(const char *searchId);
   373→  TclObj *(*arrayNextElement)(const char *searchId);
   374→  void (*arrayDoneSearch)(const char *searchId);
   375→
   376→  /* ==================================================================
   377→   * Variable Traces
   378→   *
   379→   * For vwait, trace command, etc.
   380→   * ================================================================== */
   381→
   382→  void (*traceVarAdd)(void *vars, const char *name, size_t len, int ops,
   383→                      TclTraceProc callback, void *clientData);
   384→  void (*traceVarRemove)(void *vars, const char *name, size_t len,
   385→                         TclTraceProc callback, void *clientData);
   386→
   387→  /* ==================================================================
   388→   * Commands
   389→   *
   390→   * Command lookup returns type + handle. C has static builtin table.
   391→   * Host manages procs, extensions, aliases.
   392→   * ================================================================== */
   393→
   394→  int (*cmdLookup)(void *ctx, const char *name, size_t len, TclCmdInfo *out);
   395→  void *(*procRegister)(void *ctx, const char *name, size_t len,
   396→                        TclObj *argList, TclObj *body);
   397→  int (*procGetDef)(void *handle, TclObj **argListOut, TclObj **bodyOut);
   398→  TclResult (*extInvoke)(TclInterp *interp, void *handle, int objc,
   399→                         TclObj **objv);
   400→  int (*cmdRename)(void *ctx, const char *oldName, size_t oldLen,
   401→                   const char *newName, size_t newLen);
   402→  int (*cmdDelete)(void *ctx, const char *name, size_t len);
   403→  int (*cmdExists)(void *ctx, const char *name, size_t len);
   404→  TclObj *(*cmdList)(void *ctx, const char *pattern);
   405→  void (*cmdHide)(void *ctx, const char *name, size_t len);
   406→  void (*cmdExpose)(void *ctx, const char *name, size_t len);
   407→
   408→  /* ==================================================================
   409→   * Channels (I/O)
   410→   *
   411→   * Abstract I/O handles. Host manages buffering, encoding.
   412→   * Channels can be shared between interpreters.
   413→   * ================================================================== */
   414→
   415→  TclChannel *(*chanOpen)(void *ctx, const char *name, const char *mode);
   416→  void (*chanClose)(void *ctx, TclChannel *chan);
   417→  TclChannel *(*chanStdin)(void *ctx);
   418→  TclChannel *(*chanStdout)(void *ctx);
   419→  TclChannel *(*chanStderr)(void *ctx);
   420→  int (*chanRead)(TclChannel *chan, char *buf, size_t len);
   421→  int (*chanWrite)(TclChannel *chan, const char *buf, size_t len);
   422→  TclObj *(*chanGets)(TclChannel *chan, int *eofOut);
   423→  int (*chanFlush)(TclChannel *chan);
   424→  int (*chanSeek)(TclChannel *chan, int64_t offset, int whence);
   425→  int64_t (*chanTell)(TclChannel *chan);
   426→  int (*chanEof)(TclChannel *chan);
   427→  int (*chanBlocked)(TclChannel *chan);
   428→  int (*chanConfigure)(TclChannel *chan, const char *opt, TclObj *val);
   429→  TclObj *(*chanCget)(TclChannel *chan, const char *opt);
   430→  TclObj *(*chanNames)(void *ctx, const char *pattern);
   431→  void (*chanShare)(void *fromCtx, void *toCtx, TclChannel *chan);
   432→  void (*chanTransfer)(void *fromCtx, void *toCtx, TclChannel *chan);
   433→  int (*chanTruncate)(TclChannel *chan, int64_t length);
   434→  int64_t (*chanCopy)(TclChannel *src, TclChannel *dst, int64_t size);
   435→  int64_t (*chanPending)(TclChannel *chan, int input);
   436→  int (*chanPipe)(void *ctx, TclChannel **readChan, TclChannel **writeChan);
   437→
   438→  /* ==================================================================
   439→   * Event Loop
   440→   *
   441→   * Timers, channel events, idle callbacks.
   442→   * Host owns the event loop; C commands register handlers.
   443→   * ================================================================== */
   444→
