   486→  int (*fileDelete)(const char *path, int force);
   487→  int (*fileRename)(const char *oldPath, const char *newPath, int force);
   488→  int (*fileMkdir)(const char *path);
   489→  int (*fileCopy)(const char *src, const char *dst, int force);
   490→  TclObj *(*fileDirname)(const char *path);
   491→  TclObj *(*fileTail)(const char *path);
   492→  TclObj *(*fileExtension)(const char *path);
   493→  TclObj *(*fileRootname)(const char *path);
   494→  TclObj *(*fileJoin)(TclObj **parts, size_t count);
   495→  TclObj *(*fileNormalize)(const char *path);
   496→  TclObj *(*fileSplit)(const char *path);
   497→  TclObj *(*fileType)(const char *path);
   498→  TclObj *(*glob)(const char *pattern, int types, const char *directory);
   499→  int (*filePathtype)(const char *path); /* Returns TclPathType */
   500→  TclObj *(*fileSeparator)(void);
   501→  TclObj *(*fileStat)(const char *path); /* Returns dict with
   502→                                            atime,ctime,dev,gid,ino,mode,mtime,nlink,size,type,uid
   503→                                          */
   504→  TclObj *(*fileLstat)(
   505→      const char *path); /* Like fileStat but doesn't follow symlinks */
   506→  TclObj *(*fileNativename)(const char *path);
   507→  int (*fileOwned)(const char *path);
   508→  TclObj *(*fileTempfile)(
   509→      void *ctx, const char *template,
   510→      TclObj **pathOut); /* Returns channel name, sets *pathOut to file path */
   511→  TclObj *(*fileTempdir)(const char *template);
   512→  TclObj *(*fileHome)(const char *user); /* NULL for current user */
   513→  int (*fileLink)(const char *linkName, const char *target,
   514→                  int linkType); /* linkType: TclLinkType */
   515→  TclObj *(*fileReadlink)(const char *linkName);
   516→  TclObj *(*fileSystem)(const char *path); /* Returns list: {fstype ?detail?} */
   517→  TclObj *(*fileVolumes)(void); /* Returns list of volume mount points */
   518→  TclObj *(*fileAttributes)(const char *path,
   519→                            const char *option); /* NULL option = get all */
   520→  int (*fileAttributesSet)(const char *path, const char *option, TclObj *value);
   521→
   522→  /* ==================================================================
   523→   * System
   524→   * ================================================================== */
   525→
   526→  int (*chdir)(const char *path);
   527→  TclObj *(*getcwd)(void);
   528→  TclObj *(*sysHostname)(void);
   529→  TclObj *(*sysExecutable)(void);
   530→  int (*sysPid)(void);
   531→
   532→  /* ==================================================================
   533→   * Regular Expressions
   534→   *
   535→   * Returns list of match indices, or NULL on no match.
   536→   * ================================================================== */
   537→
   538→  TclObj *(*regexMatch)(const char *pattern, size_t patLen, TclObj *str,
   539→                        int flags);
   540→  TclObj *(*regexSubst)(const char *pattern, size_t patLen, TclObj *str,
   541→                        TclObj *replacement, int flags);
   542→
   543→  /* ==================================================================
   544→   * Clock / Time
   545→   * ================================================================== */
   546→
   547→  int64_t (*clockSeconds)(void);
   548→  int64_t (*clockMillis)(void);
   549→  int64_t (*clockMicros)(void);
   550→  TclObj *(*clockFormat)(int64_t time, const char *fmt, const char *tz);
   551→  int64_t (*clockScan)(const char *str, const char *fmt, const char *tz);
   552→
   553→  /* ==================================================================
   554→   * Encoding
   555→   *
   556→   * TCL strings are Unicode. These convert at I/O boundaries.
   557→   * ================================================================== */
   558→
   559→  TclObj *(*encodingConvertTo)(const char *encoding, TclObj *str);
   560→  TclObj *(*encodingConvertFrom)(const char *encoding, TclObj *bytes);
   561→  TclObj *(*encodingNames)(void);
   562→  const char *(*encodingSystem)(void);
   563→};
   564→
   565→/* ========================================================================
   566→ * C Core API
   567→ *
   568→ * Functions implemented by the C core, called by the host.
   569→ * ======================================================================== */
   570→
   571→/* Initialize interpreter with host callbacks */
   572→TclInterp *tclInterpNew(const TclHost *host, void *hostCtx);
   573→void tclInterpFree(TclInterp *interp);
   574→
