   587→void tclSetError(TclInterp *interp, const char *msg, size_t len);
   588→void tclSetErrorCode(TclInterp *interp, TclObj *code);
   589→void tclAddErrorInfo(TclInterp *interp, const char *info, size_t len);
   590→
   591→/* Coroutine support */
   592→void *tclCoroCreate(TclInterp *interp, TclObj *name, TclObj *cmd);
   593→TclResult tclCoroResume(TclInterp *interp, void *coro, TclObj *value);
   594→TclResult tclCoroYield(TclInterp *interp, TclObj *value);
   595→void tclCoroDestroy(TclInterp *interp, void *coro);
   596→int tclCoroExists(TclInterp *interp, TclObj *name);
   597→
   598→/* Child interpreter support */
   599→TclInterp *tclInterpCreateChild(TclInterp *parent, const char *name, int safe);
   600→TclResult tclInterpEval(TclInterp *child, TclObj *script);
   601→void tclInterpAlias(TclInterp *child, const char *childCmd, TclInterp *target,
   602→                    const char *targetCmd, int objc, TclObj **prefixObjs);
   603→
   604→/* Expression evaluation (for expr command) */
   605→TclResult tclExprEval(TclInterp *interp, TclObj *expr, TclObj **resultOut);
   606→TclResult tclExprBool(TclInterp *interp, TclObj *expr, int *resultOut);
   607→
   608→/* Builtin command enumeration (for info commands, rename support) */
   609→int tclBuiltinCount(void);
   610→const char *tclBuiltinName(int index);
   611→int tclBuiltinLookup(const char *name, size_t len);
   612→
   613→/* Substitution (for subst command) */
   614→TclResult tclSubst(TclInterp *interp, TclObj *str, int flags,
   615→                   TclObj **resultOut);
   616→
