   618→  TCL_SUBST_COMMANDS = (1 << 0),
   619→  TCL_SUBST_VARIABLES = (1 << 1),
   620→  TCL_SUBST_BACKSLASH = (1 << 2),
   621→  TCL_SUBST_ALL =
   622→      (TCL_SUBST_COMMANDS | TCL_SUBST_VARIABLES | TCL_SUBST_BACKSLASH),
   623→} TclSubstFlag;
   624→
   625→typedef enum TclEvalFlag {
   626→  TCL_EVAL_LOCAL = 0,         /* Evaluate in current scope (default) */
   627→  TCL_EVAL_GLOBAL = (1 << 1), /* Evaluate in global scope */
