     1→/*
     2→ * ast.c - TCL Abstract Syntax Tree Implementation
     3→ *
     4→ * Implements AST node allocation and construction helpers.
     5→ * All nodes are allocated from the host-provided arena.
     6→ */
     7→
     8→#include "ast.h"
     9→#include "internal.h"
    10→
    11→/* ========================================================================
    12→ * Node Allocation
    13→ * ======================================================================== */
    14→
    15→TclAstNode *tclAstNew(TclInterp *interp, void *arena, TclNodeType type, int line) {
    16→    const TclHost *host = interp->host;
    17→
    18→    TclAstNode *node = host->arenaAlloc(arena, sizeof(TclAstNode), sizeof(void*));
    19→    if (!node) {
    20→        return NULL;
    21→    }
    22→
    23→    node->type = type;
    24→    node->line = line;
    25→
    26→    /* Zero-initialize the union */
    27→    for (size_t i = 0; i < sizeof(node->u); i++) {
    28→        ((char*)&node->u)[i] = 0;
    29→    }
    30→
    31→    return node;
    32→}
    33→
    34→/* ========================================================================
    35→ * Convenience Constructors
    36→ * ======================================================================== */
    37→
    38→TclAstNode *tclAstScript(TclInterp *interp, void *arena, int line) {
    39→    TclAstNode *node = tclAstNew(interp, arena, TCL_NODE_SCRIPT, line);
    40→    if (node) {
    41→        node->u.script.cmds = NULL;
    42→        node->u.script.count = 0;
    43→    }
    44→    return node;
    45→}
    46→
    47→TclAstNode *tclAstCommand(TclInterp *interp, void *arena, int line) {
    48→    TclAstNode *node = tclAstNew(interp, arena, TCL_NODE_COMMAND, line);
    49→    if (node) {
    50→        node->u.command.words = NULL;
    51→        node->u.command.count = 0;
    52→    }
    53→    return node;
    54→}
    55→
    56→TclAstNode *tclAstWord(TclInterp *interp, void *arena, int line) {
    57→    TclAstNode *node = tclAstNew(interp, arena, TCL_NODE_WORD, line);
    58→    if (node) {
    59→        node->u.word.parts = NULL;
    60→        node->u.word.count = 0;
    61→    }
    62→    return node;
    63→}
    64→
    65→TclAstNode *tclAstLiteral(TclInterp *interp, void *arena, const char *value, size_t len, int line) {
    66→    TclAstNode *node = tclAstNew(interp, arena, TCL_NODE_LITERAL, line);
    67→    if (node) {
    68→        /* Copy the string into the arena */
    69→        char *copy = interp->host->arenaAlloc(arena, len + 1, 1);
    70→        if (!copy) {
    71→            return NULL;
    72→        }
    73→        for (size_t i = 0; i < len; i++) {
    74→            copy[i] = value[i];
    75→        }
    76→        copy[len] = '\0';
    77→
    78→        node->u.literal.value = copy;
    79→        node->u.literal.len = len;
    80→    }
    81→    return node;
    82→}
    83→
    84→TclAstNode *tclAstVarSimple(TclInterp *interp, void *arena, const char *name, size_t len, int line) {
    85→    TclAstNode *node = tclAstNew(interp, arena, TCL_NODE_VAR_SIMPLE, line);
    86→    if (node) {
    87→        /* Copy the name into the arena */
    88→        char *copy = interp->host->arenaAlloc(arena, len + 1, 1);
    89→        if (!copy) {
    90→            return NULL;
    91→        }
    92→        for (size_t i = 0; i < len; i++) {
    93→            copy[i] = name[i];
    94→        }
    95→        copy[len] = '\0';
    96→
    97→        node->u.varSimple.name = copy;
    98→        node->u.varSimple.len = len;
    99→    }
   100→    return node;
   101→}
   102→
   103→TclAstNode *tclAstVarArray(TclInterp *interp, void *arena, const char *name, size_t nameLen, TclAstNode *index, int line) {
   104→    TclAstNode *node = tclAstNew(interp, arena, TCL_NODE_VAR_ARRAY, line);
   105→    if (node) {
   106→        /* Copy the name into the arena */
   107→        char *copy = interp->host->arenaAlloc(arena, nameLen + 1, 1);
   108→        if (!copy) {
   109→            return NULL;
   110→        }
   111→        for (size_t i = 0; i < nameLen; i++) {
   112→            copy[i] = name[i];
   113→        }
   114→        copy[nameLen] = '\0';
   115→
   116→        node->u.varArray.name = copy;
   117→        node->u.varArray.nameLen = nameLen;
   118→        node->u.varArray.index = index;
   119→    }
   120→    return node;
   121→}
   122→
   123→TclAstNode *tclAstCmdSubst(TclInterp *interp, void *arena, TclAstNode *script, int line) {
   124→    TclAstNode *node = tclAstNew(interp, arena, TCL_NODE_CMD_SUBST, line);
   125→    if (node) {
   126→        node->u.cmdSubst.script = script;
   127→    }
   128→    return node;
   129→}
   130→
   131→TclAstNode *tclAstBackslash(TclInterp *interp, void *arena, const char *value, size_t len, int line) {
   132→    TclAstNode *node = tclAstNew(interp, arena, TCL_NODE_BACKSLASH, line);
   133→    if (node) {
   134→        /* Copy the resolved value into the arena */
   135→        char *copy = interp->host->arenaAlloc(arena, len + 1, 1);
   136→        if (!copy) {
   137→            return NULL;
   138→        }
   139→        for (size_t i = 0; i < len; i++) {
   140→            copy[i] = value[i];
   141→        }
   142→        copy[len] = '\0';
   143→
   144→        node->u.backslash.value = copy;
   145→        node->u.backslash.len = len;
   146→    }
   147→    return node;
   148→}
   149→
   150→TclAstNode *tclAstExpand(TclInterp *interp, void *arena, TclAstNode *word, int line) {
   151→    TclAstNode *node = tclAstNew(interp, arena, TCL_NODE_EXPAND, line);
   152→    if (node) {
   153→        node->u.expand.word = word;
   154→    }
   155→    return node;
   156→}
   157→
   158→/* ========================================================================
   159→ * Child Node Management
   160→ *
   161→ * Uses a simple growth strategy: allocate new array, copy old entries.
   162→ * This is fine for arena allocation since we don't need to free old arrays.
   163→ * ======================================================================== */
   164→
   165→/* Initial capacity for child arrays */
   166→enum { AST_INITIAL_CAPACITY = 8 };
   167→
   168→int tclAstScriptAddCmd(TclInterp *interp, void *arena, TclAstNode *script, TclAstNode *cmd) {
   169→    if (script->type != TCL_NODE_SCRIPT) {
   170→        return -1;
   171→    }
   172→
   173→    const TclHost *host = interp->host;
   174→    int count = script->u.script.count;
   175→
   176→    /* Allocate new array with space for one more */
   177→    TclAstNode **newCmds = host->arenaAlloc(arena, (count + 1) * sizeof(TclAstNode*), sizeof(void*));
   178→    if (!newCmds) {
   179→        return -1;
   180→    }
   181→
   182→    /* Copy existing entries */
   183→    for (int i = 0; i < count; i++) {
   184→        newCmds[i] = script->u.script.cmds[i];
   185→    }
   186→
   187→    /* Add new entry */
   188→    newCmds[count] = cmd;
   189→
   190→    script->u.script.cmds = newCmds;
   191→    script->u.script.count = count + 1;
   192→
   193→    return 0;
   194→}
   195→
   196→int tclAstCommandAddWord(TclInterp *interp, void *arena, TclAstNode *command, TclAstNode *word) {
   197→    if (command->type != TCL_NODE_COMMAND) {
   198→        return -1;
   199→    }
   200→
   201→    const TclHost *host = interp->host;
   202→    int count = command->u.command.count;
   203→
   204→    /* Allocate new array with space for one more */
   205→    TclAstNode **newWords = host->arenaAlloc(arena, (count + 1) * sizeof(TclAstNode*), sizeof(void*));
   206→    if (!newWords) {
   207→        return -1;
   208→    }
   209→
   210→    /* Copy existing entries */
   211→    for (int i = 0; i < count; i++) {
   212→        newWords[i] = command->u.command.words[i];
   213→    }
   214→
   215→    /* Add new entry */
   216→    newWords[count] = word;
   217→
   218→    command->u.command.words = newWords;
   219→    command->u.command.count = count + 1;
   220→
   221→    return 0;
   222→}
   223→
   224→int tclAstWordAddPart(TclInterp *interp, void *arena, TclAstNode *word, TclAstNode *part) {
   225→    if (word->type != TCL_NODE_WORD) {
   226→        return -1;
   227→    }
   228→
   229→    const TclHost *host = interp->host;
   230→    int count = word->u.word.count;
   231→
   232→    /* Allocate new array with space for one more */
   233→    TclAstNode **newParts = host->arenaAlloc(arena, (count + 1) * sizeof(TclAstNode*), sizeof(void*));
   234→    if (!newParts) {
   235→        return -1;
   236→    }
   237→
   238→    /* Copy existing entries */
   239→    for (int i = 0; i < count; i++) {
   240→        newParts[i] = word->u.word.parts[i];
   241→    }
   242→
   243→    /* Add new entry */
   244→    newParts[count] = part;
   245→
   246→    word->u.word.parts = newParts;
   247→    word->u.word.count = count + 1;
   248→
   249→    return 0;
   250→}
   251→
   252→/* ========================================================================
   253→ * AST Parsing
   254→ *
   255→ * Parse TCL source into an AST. Uses the existing lexer for tokenization
   256→ * but builds tree nodes instead of TclWord arrays.
   257→ * ======================================================================== */
   258→
   259→/* Forward declarations for recursive parsing */
   260→static TclAstNode *parseScript(TclInterp *interp, void *arena, const char *script, size_t len, int *lineOut);
   261→static TclAstNode *parseCommand(TclInterp *interp, void *arena, TclLexer *lex);
   262→static TclAstNode *parseWordContent(TclInterp *interp, void *arena, const char *str, size_t len, int line, int quoted);
   263→
   264→/* Check if character is a valid variable name character */
   265→static int isVarNameChar(char c) {
   266→    return (c >= 'a' && c <= 'z') ||
   267→           (c >= 'A' && c <= 'Z') ||
   268→           (c >= '0' && c <= '9') ||
   269→           c == '_';
   270→}
   271→
   272→/* Parse a backslash escape sequence, return length consumed */
   273→static int parseBackslashEscape(const char *p, const char *end, char *out, size_t *outLen) {
   274→    if (p >= end || *p != '\\') {
   275→        return 0;
   276→    }
   277→
   278→    p++; /* Skip backslash */
   279→    if (p >= end) {
   280→        out[0] = '\\';
   281→        *outLen = 1;
   282→        return 1;
   283→    }
   284→
   285→    switch (*p) {
   286→        case 'a': out[0] = '\a'; *outLen = 1; return 2;
   287→        case 'b': out[0] = '\b'; *outLen = 1; return 2;
   288→        case 'f': out[0] = '\f'; *outLen = 1; return 2;
   289→        case 'n': out[0] = '\n'; *outLen = 1; return 2;
   290→        case 'r': out[0] = '\r'; *outLen = 1; return 2;
   291→        case 't': out[0] = '\t'; *outLen = 1; return 2;
   292→        case 'v': out[0] = '\v'; *outLen = 1; return 2;
   293→        case '\\': out[0] = '\\'; *outLen = 1; return 2;
   294→        case '"': out[0] = '"'; *outLen = 1; return 2;
   295→        case '{': out[0] = '{'; *outLen = 1; return 2;
   296→        case '}': out[0] = '}'; *outLen = 1; return 2;
   297→        case '[': out[0] = '['; *outLen = 1; return 2;
   298→        case ']': out[0] = ']'; *outLen = 1; return 2;
   299→        case '$': out[0] = '$'; *outLen = 1; return 2;
   300→        case '\n':
   301→            /* Backslash-newline: skip newline and following whitespace */
   302→            out[0] = ' ';
   303→            *outLen = 1;
   304→            p++;
   305→            while (p < end && (*p == ' ' || *p == '\t')) p++;
   306→            return (int)(p - (end - (*outLen + 1))) + 1;
   307→        case 'x': {
   308→            /* Hex escape: \xNN */
   309→            p++;
   310→            int val = 0;
   311→            int digits = 0;
   312→            while (p < end && digits < 2) {
   313→                char c = *p;
   314→                if (c >= '0' && c <= '9') val = val * 16 + (c - '0');
   315→                else if (c >= 'a' && c <= 'f') val = val * 16 + (c - 'a' + 10);
   316→                else if (c >= 'A' && c <= 'F') val = val * 16 + (c - 'A' + 10);
   317→                else break;
   318→                p++;
   319→                digits++;
   320→            }
   321→            if (digits > 0) {
   322→                out[0] = (char)val;
   323→                *outLen = 1;
   324→                return 2 + digits;
   325→            }
   326→            out[0] = 'x';
   327→            *outLen = 1;
   328→            return 2;
   329→        }
   330→        case 'u': {
   331→            /* Unicode escape: \uNNNN */
   332→            p++;
   333→            int val = 0;
   334→            int digits = 0;
   335→            while (p < end && digits < 4) {
   336→                char c = *p;
   337→                if (c >= '0' && c <= '9') val = val * 16 + (c - '0');
   338→                else if (c >= 'a' && c <= 'f') val = val * 16 + (c - 'a' + 10);
   339→                else if (c >= 'A' && c <= 'F') val = val * 16 + (c - 'A' + 10);
   340→                else break;
   341→                p++;
   342→                digits++;
   343→            }
   344→            if (digits == 4) {
   345→                /* Simple UTF-8 encoding */
   346→                if (val < 0x80) {
   347→                    out[0] = (char)val;
   348→                    *outLen = 1;
   349→                } else if (val < 0x800) {
   350→                    out[0] = (char)(0xC0 | (val >> 6));
   351→                    out[1] = (char)(0x80 | (val & 0x3F));
   352→                    *outLen = 2;
   353→                } else {
   354→                    out[0] = (char)(0xE0 | (val >> 12));
   355→                    out[1] = (char)(0x80 | ((val >> 6) & 0x3F));
   356→                    out[2] = (char)(0x80 | (val & 0x3F));
   357→                    *outLen = 3;
   358→                }
   359→                return 2 + digits;
   360→            }
   361→            out[0] = 'u';
   362→            *outLen = 1;
   363→            return 2;
   364→        }
   365→        default:
   366→            /* Check for octal */
   367→            if (*p >= '0' && *p <= '7') {
   368→                int val = 0;
   369→                int digits = 0;
   370→                while (p < end && digits < 3 && *p >= '0' && *p <= '7') {
   371→                    val = val * 8 + (*p - '0');
   372→                    p++;
   373→                    digits++;
   374→                }
   375→                out[0] = (char)val;
   376→                *outLen = 1;
   377→                return 1 + digits;
   378→            }
   379→            /* Unknown escape - keep as-is */
   380→            out[0] = *p;
   381→            *outLen = 1;
   382→            return 2;
   383→    }
   384→}
   385→
   386→/* Parse the content of a word (inside quotes or bare) into word parts */
   387→static TclAstNode *parseWordContent(TclInterp *interp, void *arena, const char *str, size_t len, int line, int quoted) {
   388→    (void)quoted; /* Currently unused, may be needed for context-specific parsing */
   389→    TclAstNode *word = tclAstWord(interp, arena, line);
   390→    if (!word) return NULL;
   391→
   392→    const char *p = str;
   393→    const char *end = str + len;
   394→    const char *literalStart = p;
   395→
   396→    while (p < end) {
   397→        if (*p == '$') {
   398→            /* Variable substitution */
   399→
   400→            /* Flush pending literal */
   401→            if (p > literalStart) {
   402→                TclAstNode *lit = tclAstLiteral(interp, arena, literalStart, p - literalStart, line);
   403→                if (!lit || tclAstWordAddPart(interp, arena, word, lit) != 0) return NULL;
   404→            }
   405→
   406→            p++; /* Skip $ */
   407→
   408→            if (p < end && *p == '{') {
   409→                /* ${varname} form */
   410→                p++;
   411→                const char *nameStart = p;
   412→                int depth = 1;
   413→                while (p < end && depth > 0) {
   414→                    if (*p == '{') depth++;
   415→                    else if (*p == '}') depth--;
   416→                    if (depth > 0) p++;
   417→                }
   418→                size_t nameLen = p - nameStart;
   419→                if (p < end) p++; /* Skip closing } */
   420→
   421→                TclAstNode *var = tclAstVarSimple(interp, arena, nameStart, nameLen, line);
   422→                if (!var || tclAstWordAddPart(interp, arena, word, var) != 0) return NULL;
   423→            } else if (p < end && isVarNameChar(*p)) {
   424→                /* $varname form */
   425→                const char *nameStart = p;
   426→                while (p < end && isVarNameChar(*p)) p++;
   427→                size_t nameLen = p - nameStart;
   428→
   429→                /* Check for array index */
   430→                if (p < end && *p == '(') {
   431→                    p++; /* Skip ( */
   432→                    const char *indexStart = p;
   433→                    int depth = 1;
   434→                    while (p < end && depth > 0) {
   435→                        if (*p == '(') depth++;
   436→                        else if (*p == ')') depth--;
   437→                        if (depth > 0) p++;
   438→                    }
   439→                    size_t indexLen = p - indexStart;
   440→                    if (p < end) p++; /* Skip closing ) */
   441→
   442→                    /* Parse index as a word (may contain substitutions) */
   443→                    TclAstNode *indexWord = parseWordContent(interp, arena, indexStart, indexLen, line, 1);
   444→                    TclAstNode *var = tclAstVarArray(interp, arena, nameStart, nameLen, indexWord, line);
   445→                    if (!var || tclAstWordAddPart(interp, arena, word, var) != 0) return NULL;
   446→                } else {
   447→                    TclAstNode *var = tclAstVarSimple(interp, arena, nameStart, nameLen, line);
   448→                    if (!var || tclAstWordAddPart(interp, arena, word, var) != 0) return NULL;
   449→                }
   450→            } else {
   451→                /* Lone $ - treat as literal */
   452→                TclAstNode *lit = tclAstLiteral(interp, arena, "$", 1, line);
   453→                if (!lit || tclAstWordAddPart(interp, arena, word, lit) != 0) return NULL;
   454→            }
   455→            literalStart = p;
   456→
   457→        } else if (*p == '[') {
   458→            /* Command substitution */
   459→
   460→            /* Flush pending literal */
   461→            if (p > literalStart) {
   462→                TclAstNode *lit = tclAstLiteral(interp, arena, literalStart, p - literalStart, line);
   463→                if (!lit || tclAstWordAddPart(interp, arena, word, lit) != 0) return NULL;
   464→            }
   465→
   466→            p++; /* Skip [ */
   467→            const char *cmdStart = p;
   468→            int depth = 1;
   469→            while (p < end && depth > 0) {
   470→                if (*p == '[') depth++;
   471→                else if (*p == ']') depth--;
   472→                else if (*p == '{') {
   473→                    /* Skip braced content */
   474→                    int braceDepth = 1;
   475→                    p++;
   476→                    while (p < end && braceDepth > 0) {
   477→                        if (*p == '{') braceDepth++;
   478→                        else if (*p == '}') braceDepth--;
   479→                        p++;
   480→                    }
   481→                    continue;
   482→                } else if (*p == '"') {
   483→                    /* Skip quoted content */
   484→                    p++;
   485→                    while (p < end && *p != '"') {
   486→                        if (*p == '\\' && p + 1 < end) p++;
   487→                        p++;
   488→                    }
   489→                    if (p < end) p++;
   490→                    continue;
   491→                }
   492→                if (depth > 0) p++;
   493→            }
   494→            size_t cmdLen = p - cmdStart;
   495→            if (p < end) p++; /* Skip closing ] */
   496→
   497→            /* Parse the command substitution as a script */
   498→            int dummy;
   499→            TclAstNode *script = parseScript(interp, arena, cmdStart, cmdLen, &dummy);
   500→            TclAstNode *cmdSubst = tclAstCmdSubst(interp, arena, script, line);
   501→            if (!cmdSubst || tclAstWordAddPart(interp, arena, word, cmdSubst) != 0) return NULL;
   502→
   503→            literalStart = p;
   504→
   505→        } else if (*p == '\\') {
   506→            /* Backslash escape */
   507→
   508→            /* Flush pending literal */
   509→            if (p > literalStart) {
   510→                TclAstNode *lit = tclAstLiteral(interp, arena, literalStart, p - literalStart, line);
   511→                if (!lit || tclAstWordAddPart(interp, arena, word, lit) != 0) return NULL;
   512→            }
   513→
   514→            char buf[4];
   515→            size_t bufLen;
   516→            int consumed = parseBackslashEscape(p, end, buf, &bufLen);
   517→
   518→            TclAstNode *bs = tclAstBackslash(interp, arena, buf, bufLen, line);
   519→            if (!bs || tclAstWordAddPart(interp, arena, word, bs) != 0) return NULL;
   520→
   521→            p += consumed;
   522→            literalStart = p;
   523→
   524→        } else {
   525→            p++;
   526→        }
   527→    }
   528→
   529→    /* Flush final literal */
   530→    if (p > literalStart) {
   531→        TclAstNode *lit = tclAstLiteral(interp, arena, literalStart, p - literalStart, line);
   532→        if (!lit || tclAstWordAddPart(interp, arena, word, lit) != 0) return NULL;
   533→    }
   534→
   535→    /* Optimize: if word has only one literal part, return just the literal */
   536→    if (word->u.word.count == 1 && word->u.word.parts[0]->type == TCL_NODE_LITERAL) {
   537→        return word->u.word.parts[0];
   538→    }
   539→
   540→    /* If word has no parts, return empty literal */
   541→    if (word->u.word.count == 0) {
   542→        return tclAstLiteral(interp, arena, "", 0, line);
   543→    }
   544→
   545→    return word;
   546→}
   547→
   548→/* Parse a braced word - no substitution */
   549→static TclAstNode *parseBracedWord(TclInterp *interp, void *arena, const char *str, size_t len, int line) {
   550→    /* Content between braces is literal, skip outer braces */
   551→    if (len >= 2 && str[0] == '{' && str[len-1] == '}') {
   552→        return tclAstLiteral(interp, arena, str + 1, len - 2, line);
   553→    }
   554→    return tclAstLiteral(interp, arena, str, len, line);
   555→}
   556→
   557→/* Parse a single command */
   558→static TclAstNode *parseCommand(TclInterp *interp, void *arena, TclLexer *lex) {
   559→    TclAstNode *cmd = tclAstCommand(interp, arena, lex->line);
   560→    if (!cmd) return NULL;
   561→
   562→    while (!tclLexerAtCommandEnd(lex) && !tclLexerAtEnd(lex)) {
   563→        TclWord word;
   564→        int r = tclLexerNextWord(lex, &word, interp);
   565→        if (r != 0) break;
   566→
   567→        TclAstNode *wordNode;
   568→
   569→        /* Check for {*} expansion */
   570→        int isExpand = 0;
   571→        const char *wordStart = word.start;
   572→        size_t wordLen = word.len;
   573→
   574→        if (word.type == TCL_WORD_BARE && wordLen >= 3 &&
   575→            wordStart[0] == '{' && wordStart[1] == '*' && wordStart[2] == '}') {
   576→            isExpand = 1;
   577→            wordStart += 3;
   578→            wordLen -= 3;
   579→        }
   580→
   581→        if (word.type == TCL_WORD_BRACES) {
   582→            /* Braced - no substitution */
   583→            wordNode = parseBracedWord(interp, arena, word.start, word.len, word.line);
   584→        } else if (word.type == TCL_WORD_QUOTES) {
   585→            /* Quoted - lexer already stripped quotes, parse content directly */
   586→            wordNode = parseWordContent(interp, arena, word.start, word.len, word.line, 1);
   587→        } else {
   588→            /* Bare word */
   589→            if (isExpand) {
   590→                wordNode = parseWordContent(interp, arena, wordStart, wordLen, word.line, 0);
   591→            } else {
   592→                wordNode = parseWordContent(interp, arena, word.start, word.len, word.line, 0);
   593→            }
   594→        }
   595→
   596→        if (!wordNode) return NULL;
   597→
   598→        /* Wrap in expand node if needed */
   599→        if (isExpand) {
   600→            wordNode = tclAstExpand(interp, arena, wordNode, word.line);
   601→            if (!wordNode) return NULL;
   602→        }
   603→
   604→        if (tclAstCommandAddWord(interp, arena, cmd, wordNode) != 0) return NULL;
   605→    }
   606→
   607→    /* Skip command terminator */
   608→    if (!tclLexerAtEnd(lex)) {
   609→        if (*lex->pos == ';' || *lex->pos == '\n') {
   610→            if (*lex->pos == '\n') lex->line++;
   611→            lex->pos++;
   612→        }
   613→    }
   614→
   615→    return cmd;
   616→}
   617→
   618→/* Parse a complete script */
   619→static TclAstNode *parseScript(TclInterp *interp, void *arena, const char *script, size_t len, int *lineOut) {
   620→    TclLexer lex;
   621→    tclLexerInit(&lex, script, len);
   622→
   623→    TclAstNode *scriptNode = tclAstScript(interp, arena, lex.line);
   624→    if (!scriptNode) return NULL;
   625→
   626→    while (!tclLexerAtEnd(&lex)) {
   627→        /* Skip whitespace and blank lines */
   628→        tclLexerSkipSpace(&lex);
   629→
   630→        if (tclLexerAtEnd(&lex)) break;
   631→
   632→        /* Skip comments */
   633→        if (tclLexerAtComment(&lex)) {
   634→            while (!tclLexerAtEnd(&lex) && *lex.pos != '\n') {
   635→                lex.pos++;
   636→            }
   637→            if (!tclLexerAtEnd(&lex)) {
   638→                lex.line++;
   639→                lex.pos++;
   640→            }
   641→            continue;
   642→        }
   643→
   644→        /* Skip empty commands */
   645→        if (*lex.pos == ';' || *lex.pos == '\n') {
   646→            if (*lex.pos == '\n') lex.line++;
   647→            lex.pos++;
   648→            continue;
   649→        }
   650→
   651→        TclAstNode *cmd = parseCommand(interp, arena, &lex);
   652→        if (!cmd) return NULL;
   653→
   654→        /* Only add non-empty commands */
   655→        if (cmd->u.command.count > 0) {
   656→            if (tclAstScriptAddCmd(interp, arena, scriptNode, cmd) != 0) return NULL;
   657→        }
   658→    }
   659→
   660→    if (lineOut) *lineOut = lex.line;
   661→    return scriptNode;
   662→}
   663→
   664→/* ========================================================================
   665→ * Public Parsing API
   666→ * ======================================================================== */
   667→
   668→TclAstNode *tclAstParse(TclInterp *interp, void *arena, const char *script, size_t len) {
   669→    int line;
   670→    return parseScript(interp, arena, script, len, &line);
   671→}
   672→
   673→TclAstNode *tclAstParseWord(TclInterp *interp, void *arena, const char *word, size_t len, int quoted) {
   674→    return parseWordContent(interp, arena, word, len, 1, quoted);
   675→}
