    99→/* Helper to apply a single index to current value */
   100→static TclObj *applyIndex(const TclHost *host, TclObj *current, TclObj *indexObj) {
   101→    size_t idxStrLen;
   102→    host->getStringPtr(indexObj, &idxStrLen);
   103→    if (idxStrLen == 0) {
   104→        return current;  /* Empty index, return as-is */
   105→    }
   106→
   107→    size_t listLen = host->listLength(current);
   108→    size_t idx;
   109→
   110→    if (parseListIndex(host, indexObj, listLen, &idx) != 0) {
   111→        return NULL;  /* Out of bounds or invalid */
   112→    }
   113→
   114→    return host->listIndex(current, idx);
   115→}
