   170→/* ========================================================================
   171→ * lrange Command
   172→ * ======================================================================== */
   173→
   174→TclResult tclCmdLrange(TclInterp *interp, int objc, TclObj **objv) {
   175→    const TclHost *host = interp->host;
   176→
   177→    if (objc != 4) {
   178→        tclSetError(interp, "wrong # args: should be \"lrange list first last\"", -1);
   179→        return TCL_ERROR;
   180→    }
   181→
   182→    size_t listLen = host->listLength(objv[1]);
   183→
   184→    /* Parse first index */
   185→    size_t first = 0;
   186→    if (parseListIndex(host, objv[2], listLen, &first) != 0) {
   187→        /* Check if it's negative - treat as 0 */
   188→        int64_t val;
   189→        if (host->asInt(objv[2], &val) == 0 && val < 0) {
   190→            first = 0;
   191→        } else {
   192→            /* Check for end-N that went negative */
   193→            size_t idxLen;
   194→            const char *idxStr = host->getStringPtr(objv[2], &idxLen);
   195→            if (idxLen >= 5 && tclStrncmp(idxStr, "end-", 4) == 0) {
   196→                first = 0;  /* end-N that goes negative -> treat as 0 for first */
   197→            } else if (listLen == 0) {
   198→                tclSetResult(interp, host->newString("", 0));
   199→                return TCL_OK;
   200→            }
   201→        }
   202→    }
   203→
   204→    /* Parse last index */
   205→    size_t last = listLen > 0 ? listLen - 1 : 0;
   206→    int lastNegative = 0;
   207→    if (parseListIndex(host, objv[3], listLen, &last) != 0) {
   208→        /* Check if it's past end - treat as end */
   209→        int64_t val;
   210→        if (host->asInt(objv[3], &val) == 0) {
   211→            if (val >= (int64_t)listLen) {
   212→                last = listLen > 0 ? listLen - 1 : 0;
   213→            } else if (val < 0) {
   214→                lastNegative = 1;
   215→            }
   216→        } else {
   217→            /* Check for end-N that went negative */
   218→            size_t idxLen;
   219→            const char *idxStr = host->getStringPtr(objv[3], &idxLen);
   220→            if (idxLen >= 5 && tclStrncmp(idxStr, "end-", 4) == 0) {
   221→                lastNegative = 1;  /* end-N that goes negative for last */
   222→            }
   223→        }
   224→    }
   225→
   226→    /* If last is negative or first > last, return empty */
   227→    if (lastNegative || first > last || listLen == 0) {
   228→        tclSetResult(interp, host->newString("", 0));
   229→        return TCL_OK;
   230→    }
   231→
   232→    /* Use host's listRange */
   233→    TclObj *result = host->listRange(objv[1], first, last);
   234→    tclSetResult(interp, result);
   235→    return TCL_OK;
   236→}
