   238→/* ========================================================================
   239→ * lappend Command
   240→ * ======================================================================== */
   241→
   242→TclResult tclCmdLappend(TclInterp *interp, int objc, TclObj **objv) {
   243→    const TclHost *host = interp->host;
   244→
   245→    if (objc < 2) {
   246→        tclSetError(interp, "wrong # args: should be \"lappend varName ?value ...?\"", -1);
   247→        return TCL_ERROR;
   248→    }
   249→
   250→    /* Get variable name */
   251→    size_t varLen;
   252→    const char *varName = host->getStringPtr(objv[1], &varLen);
   253→    void *vars = interp->currentFrame->varsHandle;
   254→
   255→    /* Get current value or empty list */
   256→    TclObj *current = host->varGet(vars, varName, varLen);
   257→    if (!current) {
   258→        current = host->newString("", 0);
   259→        /* If variable doesn't exist, create it */
   260→        host->varSet(vars, varName, varLen, host->dup(current));
   261→    }
   262→
   263→    /* If no values to append, just return current value */
   264→    if (objc == 2) {
   265→        tclSetResult(interp, host->dup(current));
   266→        return TCL_OK;
   267→    }
   268→
   269→    /* Append each value */
   270→    TclObj *result = current;
   271→    for (int i = 2; i < objc; i++) {
   272→        result = host->listAppend(result, objv[i]);
   273→    }
   274→
   275→    /* Set the variable */
   276→    host->varSet(vars, varName, varLen, result);
   277→
   278→    tclSetResult(interp, host->dup(result));
   279→    return TCL_OK;
   280→}
