   427→/* ========================================================================
   428→ * lsort Command
   429→ * ======================================================================== */
   430→
   431→TclResult tclCmdLsort(TclInterp *interp, int objc, TclObj **objv) {
   432→    const TclHost *host = interp->host;
   433→
   434→    if (objc < 2) {
   435→        tclSetError(interp, "wrong # args: should be \"lsort ?options? list\"", -1);
   436→        return TCL_ERROR;
   437→    }
   438→
   439→    /* Parse options */
   440→    /* flags: 1=decreasing, 2=integer, 4=nocase, 8=unique, 16=dictionary, 32=real */
   441→    int flags = 0;
   442→    int listIdx = objc - 1;  /* List is last argument */
   443→
   444→    for (int i = 1; i < objc - 1; i++) {
   445→        size_t optLen;
   446→        const char *opt = host->getStringPtr(objv[i], &optLen);
   447→
   448→        if (optLen > 0 && opt[0] == '-') {
   449→            if (optLen == 11 && tclStrncmp(opt, "-decreasing", 11) == 0) {
   450→                flags |= 1;
   451→            } else if (optLen == 11 && tclStrncmp(opt, "-increasing", 11) == 0) {
   452→                flags &= ~1;
   453→            } else if (optLen == 8 && tclStrncmp(opt, "-integer", 8) == 0) {
   454→                flags |= 2;
   455→                flags &= ~(16 | 32);  /* Clear dictionary and real */
   456→            } else if (optLen == 6 && tclStrncmp(opt, "-ascii", 6) == 0) {
   457→                flags &= ~(2 | 16 | 32);  /* Clear integer, dictionary, real */
   458→            } else if (optLen == 7 && tclStrncmp(opt, "-nocase", 7) == 0) {
   459→                flags |= 4;
   460→            } else if (optLen == 7 && tclStrncmp(opt, "-unique", 7) == 0) {
   461→                flags |= 8;
   462→            } else if (optLen == 11 && tclStrncmp(opt, "-dictionary", 11) == 0) {
   463→                flags |= 16;
   464→                flags &= ~(2 | 32);  /* Clear integer and real */
   465→            } else if (optLen == 5 && tclStrncmp(opt, "-real", 5) == 0) {
   466→                flags |= 32;
   467→                flags &= ~(2 | 16);  /* Clear integer and dictionary */
   468→            }
   469→        }
   470→    }
   471→
   472→    TclObj *result = host->listSort(objv[listIdx], flags);
   473→    tclSetResult(interp, result);
   474→    return TCL_OK;
   475→}
