 * TclObj* tclGetResult(TclInterp *interp);
 * void tclSetResult(TclInterp *interp, TclObj *result);
 */

/* ========================================================================
 * Helper Macros
 * ======================================================================== */

/* String comparison helpers for command lookup */
#define TCL_STREQ(a, alen, b) \
    ((alen) == sizeof(b) - 1 && tclStrncmp((a), (b), (alen)) == 0)

