    13→TclResult tclCmdList(TclInterp *interp, int objc, TclObj **objv) {
    14→    const TclHost *host = interp->host;
    15→
    16→    /* Create a list from all arguments (skip command name) */
    17→    if (objc <= 1) {
    18→        /* Empty list */
