    32→TclResult tclCmdLlength(TclInterp *interp, int objc, TclObj **objv) {
    33→    const TclHost *host = interp->host;
    34→
    35→    if (objc != 2) {
    36→        tclSetError(interp, "wrong # args: should be \"llength list\"", -1);
