    49→/* Parse an index value (supports "end", "end-N", and integers) */
    50→static int parseListIndex(const TclHost *host, TclObj *indexObj, size_t listLen, size_t *out) {
    51→    size_t idxLen;
    52→    const char *idxStr = host->getStringPtr(indexObj, &idxLen);
    53→
    54→    /* Check for "end" */
    55→    if (idxLen >= 3 && tclStrncmp(idxStr, "end", 3) == 0) {
    56→        if (listLen == 0) {
    57→            *out = 0;
    58→            return -1;  /* Out of bounds */
    59→        }
    60→        if (idxLen == 3) {
    61→            *out = listLen - 1;
    62→            return 0;
    63→        }
    64→        /* end-N or end+N */
    65→        if (idxLen > 4 && idxStr[3] == '-') {
    66→            int64_t offset = 0;
    67→            for (size_t i = 4; i < idxLen; i++) {
    68→                if (idxStr[i] >= '0' && idxStr[i] <= '9') {
    69→                    offset = offset * 10 + (idxStr[i] - '0');
    70→                } else {
    71→                    return -1;
    72→                }
    73→            }
    74→            if (offset > (int64_t)(listLen - 1)) {
    75→                *out = 0;
    76→                return -1;
    77→            }
    78→            *out = listLen - 1 - (size_t)offset;
    79→            return 0;
    80→        }
    81→        return -1;
    82→    }
    83→
    84→    /* Try as integer */
    85→    int64_t idx;
    86→    if (host->asInt(indexObj, &idx) != 0) {
    87→        return -1;
    88→    }
    89→
    90→    if (idx < 0 || (size_t)idx >= listLen) {
    91→        *out = 0;
    92→        return -1;  /* Out of bounds */
    93→    }
    94→
    95→    *out = (size_t)idx;
    96→    return 0;
    97→}
