   117→TclResult tclCmdLindex(TclInterp *interp, int objc, TclObj **objv) {
   118→    const TclHost *host = interp->host;
   119→
   120→    if (objc < 2) {
   121→        tclSetError(interp, "wrong # args: should be \"lindex list ?index ...?\"", -1);
   122→        return TCL_ERROR;
   123→    }
   124→
   125→    /* If no index, return the list itself */
   126→    if (objc == 2) {
   127→        tclSetResult(interp, host->dup(objv[1]));
   128→        return TCL_OK;
   129→    }
   130→
   131→    /* Start with the list */
   132→    TclObj *current = objv[1];
   133→
   134→    /* Process each index argument */
   135→    for (int i = 2; i < objc; i++) {
   136→        size_t idxStrLen;
   137→        host->getStringPtr(objv[i], &idxStrLen);
   138→
   139→        /* Empty index - return current value */
   140→        if (idxStrLen == 0) {
   141→            continue;
   142→        }
   143→
   144→        /* Check if this index argument is itself a list of indices */
   145→        TclObj **idxElems;
   146→        size_t idxCount;
   147→        if (host->asList(objv[i], &idxElems, &idxCount) == 0 && idxCount > 1) {
   148→            /* It's a list of indices - apply each one */
   149→            for (size_t j = 0; j < idxCount; j++) {
   150→                current = applyIndex(host, current, idxElems[j]);
   151→                if (!current) {
   152→                    tclSetResult(interp, host->newString("", 0));
   153→                    return TCL_OK;
   154→                }
   155→            }
   156→        } else {
   157→            /* Single index */
   158→            current = applyIndex(host, current, objv[i]);
   159→            if (!current) {
   160→                tclSetResult(interp, host->newString("", 0));
   161→                return TCL_OK;
   162→            }
   163→        }
   164→    }
   165→
   166→    tclSetResult(interp, host->dup(current));
   167→    return TCL_OK;
   168→}
