   282→/* ========================================================================
   283→ * join Command
   284→ * ======================================================================== */
   285→
   286→TclResult tclCmdJoin(TclInterp *interp, int objc, TclObj **objv) {
   287→    const TclHost *host = interp->host;
   288→
   289→    if (objc < 2 || objc > 3) {
   290→        tclSetError(interp, "wrong # args: should be \"join list ?joinString?\"", -1);
   291→        return TCL_ERROR;
   292→    }
   293→
   294→    /* Get join string (default is space) */
   295→    const char *joinStr = " ";
   296→    size_t joinLen = 1;
   297→    if (objc == 3) {
   298→        joinStr = host->getStringPtr(objv[2], &joinLen);
   299→    }
   300→
   301→    /* Parse list */
   302→    TclObj **elems;
   303→    size_t elemCount;
   304→    if (host->asList(objv[1], &elems, &elemCount) != 0) {
   305→        tclSetError(interp, "invalid list", -1);
   306→        return TCL_ERROR;
   307→    }
   308→
   309→    /* Empty list returns empty string */
   310→    if (elemCount == 0) {
   311→        tclSetResult(interp, host->newString("", 0));
   312→        return TCL_OK;
   313→    }
   314→
   315→    /* Calculate total length */
   316→    size_t totalLen = 0;
   317→    for (size_t i = 0; i < elemCount; i++) {
   318→        size_t len;
   319→        host->getStringPtr(elems[i], &len);
   320→        totalLen += len;
   321→        if (i > 0) totalLen += joinLen;
   322→    }
   323→
   324→    /* Build result using arena */
   325→    void *arena = host->arenaPush(interp->hostCtx);
   326→    char *buf = host->arenaAlloc(arena, totalLen + 1, 1);
   327→    char *p = buf;
   328→
   329→    for (size_t i = 0; i < elemCount; i++) {
   330→        if (i > 0) {
   331→            for (size_t j = 0; j < joinLen; j++) {
   332→                *p++ = joinStr[j];
   333→            }
   334→        }
   335→        size_t len;
   336→        const char *s = host->getStringPtr(elems[i], &len);
   337→        for (size_t j = 0; j < len; j++) {
   338→            *p++ = s[j];
   339→        }
   340→    }
   341→    *p = '\0';
   342→
   343→    TclObj *result = host->newString(buf, totalLen);
   344→    host->arenaPop(interp->hostCtx, arena);
   345→    tclSetResult(interp, result);
   346→    return TCL_OK;
   347→}
