   349→/* ========================================================================
   350→ * split Command
   351→ * ======================================================================== */
   352→
   353→TclResult tclCmdSplit(TclInterp *interp, int objc, TclObj **objv) {
   354→    const TclHost *host = interp->host;
   355→
   356→    if (objc < 2 || objc > 3) {
   357→        tclSetError(interp, "wrong # args: should be \"split string ?splitChars?\"", -1);
   358→        return TCL_ERROR;
   359→    }
   360→
   361→    /* Get string to split */
   362→    size_t strLen;
   363→    const char *str = host->getStringPtr(objv[1], &strLen);
   364→
   365→    /* Get split characters (default is whitespace) */
   366→    const char *splitChars = " \t\n\r";
   367→    size_t splitLen = 4;
   368→    if (objc == 3) {
   369→        splitChars = host->getStringPtr(objv[2], &splitLen);
   370→    }
   371→
   372→    /* Empty string returns empty list */
   373→    if (strLen == 0) {
   374→        tclSetResult(interp, host->newString("", 0));
   375→        return TCL_OK;
   376→    }
   377→
   378→    /* If splitChars is empty, split into individual characters */
   379→    if (splitLen == 0) {
   380→        void *arena = host->arenaPush(interp->hostCtx);
   381→        TclObj **elems = host->arenaAlloc(arena, strLen * sizeof(TclObj*), sizeof(void*));
   382→        for (size_t i = 0; i < strLen; i++) {
   383→            elems[i] = host->newString(&str[i], 1);
   384→        }
   385→        TclObj *result = host->newList(elems, strLen);
   386→        host->arenaPop(interp->hostCtx, arena);
   387→        tclSetResult(interp, result);
   388→        return TCL_OK;
   389→    }
   390→
   391→    /* Count elements first */
   392→    size_t maxElems = strLen + 1;  /* Maximum possible elements */
   393→    void *arena = host->arenaPush(interp->hostCtx);
   394→    TclObj **elems = host->arenaAlloc(arena, maxElems * sizeof(TclObj*), sizeof(void*));
   395→    size_t elemCount = 0;
   396→
   397→    /* Helper to check if char is a split char */
   398→    const char *start = str;
   399→    const char *end = str + strLen;
   400→    const char *p = str;
   401→
   402→    while (p <= end) {
   403→        int isSplit = 0;
   404→        if (p < end) {
   405→            for (size_t i = 0; i < splitLen; i++) {
   406→                if (*p == splitChars[i]) {
   407→                    isSplit = 1;
   408→                    break;
   409→                }
   410→            }
   411→        }
   412→
   413→        if (isSplit || p == end) {
   414→            /* Add element from start to p */
   415→            elems[elemCount++] = host->newString(start, p - start);
   416→            start = p + 1;
   417→        }
   418→        p++;
   419→    }
   420→
   421→    TclObj *result = host->newList(elems, elemCount);
   422→    host->arenaPop(interp->hostCtx, arena);
   423→    tclSetResult(interp, result);
   424→    return TCL_OK;
   425→}
