tests.script:
# Test: basic for loop
for {set i 0} {$i < 5} {set i [expr $i + 1]} {
    puts $i
}

# Test: for loop with break
for {set i 0} {$i < 10} {set i [expr $i + 1]} {
    if {$i == 3} {
        break
    }
    puts $i
}

# Test: for loop with continue
for {set i 0} {$i < 5} {set i [expr $i + 1]} {
    if {$i == 2} {
        continue
    }
    puts $i
}

# Test: basic foreach
foreach x {a b c} {
    puts $x
}

# Test: foreach with numbers
foreach n {1 2 3 4 5} {
    puts [expr $n * $n]
}

# Test: foreach with break
foreach x {a b c d e} {
    if {$x == "c"} {
        break
    }
    puts $x
}

# Test: basic if true condition
if {1} {
    puts "true"
}

# Test: if-else with false condition
if {0} {
    puts "true"
} else {
    puts "false"
}

# Test: if-elseif-else chain
set x 2
if {$x == 1} {
    puts "one"
} elseif {$x == 2} {
    puts "two"
} else {
    puts "other"
}

# Test: if with comparison operators
set a 5
set b 10
if {$a < $b} { puts "a < b" }
if {$a > $b} { puts "a > b" }
if {$a <= $b} { puts "a <= b" }
if {$a != $b} { puts "a != b" }

# Test: basic while loop
set i 0
while {$i < 3} {
    puts $i
    set i [expr $i + 1]
}

# Test: while with break
set i 0
while {1} {
    if {$i >= 3} {
        break
    }
    puts $i
    set i [expr $i + 1]
}
