tests.script:
# Test: error in conditional - error not reached
if {0} {
    error "this should not happen"
}
puts "ok"

# Test: error in proc
proc fail {} {
    error "proc failed"
}
catch {fail} msg
puts "msg: $msg"

# Test: errorInfo variable after error
catch {error "test error"}
puts "errorInfo exists: [info exists ::errorInfo]"

# Test: errorCode variable after error with code
catch {error "test" "" {POSIX ENOENT}}
puts "errorCode exists: [info exists ::errorCode]"

# Test: nested proc error
proc inner {} {
    error "inner error"
}
proc outer {} {
    inner
}
catch {outer} msg
puts "msg: $msg"
