tests.script:
# Test: brace quoting preserves literal content
# Braces prevent substitution and preserve whitespace

puts {hello world}
puts {hello   world}
puts {$notavar}
puts {[notacmd]}

# Test: nested braces
# Braces can be nested and preserve internal braces

puts {a{b}c}
puts {{nested}}
puts {a{b{c}d}e}
puts {open { close }}
