tests.script:
# Test: join flattens list by one level
set data {1 {2 3} 4 {5 {6 7} 8}}
puts [join $data]

# Test: join with multi-character separator
set data {1 2 3 4 5}
puts [join $data ", "]

# Test: lappend with no values returns current value
set x {a b c}
puts [lappend x]

# Test: lappend returns the new list value
set x {a}
puts [lappend x b c]

# Test: lappend treats list value as single element
set x {a}
lappend x {b c d}
puts $x
puts [llength $x]

# Test: lappend with special characters needing quoting
set x {a}
lappend x {$var} {[cmd]}
puts $x

# Test: llength of whitespace-only string is 0
set var { }
puts [llength $var]
