tests.script:
# Test: uplevel command - basic usage
proc test {} {
    set x 10
    uplevel 1 {set y 20}
}
test
puts "y = $y"

# Test: uplevel command - access caller's variables
set x 100
proc test {} {
    uplevel 1 {puts "x = $x"}
}
test

# Test: uplevel command - level #0 refers to global
proc test {} {
    uplevel #0 {set result "from global"}
}
test
puts "result = $result"
